// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [loading, setLoading] = useState(true);

  // 1. Carica i dati dal backend all'avvio
  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  // 2. Gestione dell'upload del file Excel
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames(); // Ricarica la lista aggiornata dal server
      alert("Lista aggiornata correttamente!");
    } catch (err) {
      alert("Errore nell'upload del file");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 3. Logica dei Filtri (Ricerca + Saghe)
  const sagheUniche = ['Tutte', ...new Set(games.map(g => g.saga))].filter(Boolean);

  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesSaga = selectedSaga === 'Tutte' || game.saga === selectedSaga;
    return matchesSearch && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#121212', color: 'white', fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif' }}>
      
      {/* Sidebar Filtro Saghe */}
      <div style={{ 
        width: '250px', 
        padding: '20px', 
        borderRight: '1px solid #333', 
        position: 'sticky', 
        top: 0, 
        height: '100vh', 
        overflowY: 'auto',
        backgroundColor: '#181818' 
      }}>
        <h3 style={{ borderBottom: '1px solid #333', paddingBottom: '10px', color: '#00d1b2' }}>Saghe</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {sagheUniche.map(s => (
            <li 
              key={s} 
              onClick={() => setSelectedSaga(s)}
              style={{ 
                cursor: 'pointer', 
                padding: '10px 8px', 
                borderRadius: '4px',
                marginBottom: '4px',
                transition: '0.2s',
                backgroundColor: selectedSaga === s ? '#00d1b222' : 'transparent',
                color: selectedSaga === s ? '#00d1b2' : '#bbb',
                fontWeight: selectedSaga === s ? 'bold' : 'normal'
              }}
              onMouseEnter={(e) => e.target.style.backgroundColor = selectedSaga === s ? '#00d1b222' : '#252525'}
              onMouseLeave={(e) => e.target.style.backgroundColor = selectedSaga === s ? '#00d1b222' : 'transparent'}
            >
              {s}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        
        {/* Header con Ricerca e Upload */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', gap: '20px' }}>
          <input 
            type="text" 
            placeholder="Cerca un titolo tra i tuoi giochi..." 
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ 
              flex: 1, 
              padding: '15px', 
              background: '#222', 
              color: 'white', 
              border: '1px solid #444', 
              borderRadius: '8px',
              fontSize: '16px'
            }}
          />
          <div style={{ textAlign: 'right' }}>
            <label style={{ fontSize: '12px', display: 'block', marginBottom: '5px', color: '#888' }}>Aggiorna Database (.xlsx)</label>
            <input type="file" accept=".xlsx" onChange={handleFileUpload} style={{ fontSize: '13px' }} />
          </div>
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', marginTop: '50px', color: '#00d1b2' }}>Caricamento in corso...</div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '25px' }}>
            {filteredGames.map((game, i) => (
              <div key={i} style={{ 
                background: '#1e1e1e', 
                borderRadius: '12px', 
                overflow: 'hidden', 
                border: '1px solid #333',
                transition: 'transform 0.2s',
                boxShadow: '0 4px 10px rgba(0,0,0,0.3)'
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.03)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
              >
                <img 
                  src={game.copertina} 
                  alt={game.titolo} 
                  style={{ width: '100%', height: '280px', objectFit: 'cover' }} 
                  onError={(e) => { e.target.src = 'https://via.placeholder.com/200x280?text=No+Cover'; }}
                />
                <div style={{ padding: '15px' }}>
                  <h4 style={{ margin: '0 0 8px 0', fontSize: '16px', lineHeight: '1.2' }}>{game.titolo}</h4>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <small style={{ color: '#00d1b2', fontWeight: 'bold' }}>{game.piattaforma}</small>
                    <small style={{ color: '#777' }}>{game.annoUscita}</small>
                  </div>
                  {game.saga !== "Singolo" && (
                    <div style={{ fontSize: '11px', marginTop: '10px', color: '#888', fontStyle: 'italic', borderTop: '1px solid #2a2a2a', paddingTop: '8px' }}>
                      Saga: {game.saga}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {!loading && filteredGames.length === 0 && (
          <div style={{ textAlign: 'center', color: '#666', marginTop: '100px' }}>
            Nessun gioco trovato con questi filtri.
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
