import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);
  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => JSON.parse(localStorage.getItem('spinHistory')) || []);
  const [newGame, setNewGame] = useState({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato' });

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey && e.key.toLowerCase() === 'a') {
        if (prompt("Accesso protetto. Inserisci codice:") === "listone") setIsAdmin(true);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  useEffect(() => { localStorage.setItem('spinHistory', JSON.stringify(history)); }, [history]);

  const fetchGames = async () => {
    const url = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/games' : '/games.json';
    try {
      const res = await axios.get(url);
      setGames(res.data);
    } catch (err) {
      if (url.includes('5000')) { // Fallback se il server locale Ã¨ spento
        const local = await axios.get('/games.json');
        setGames(local.data);
      }
    }
  };

  useEffect(() => { fetchGames(); }, []);

  const updateList = async (newList) => {
    setGames(newList);
    if (window.location.hostname === 'localhost') await axios.post('http://localhost:5000/api/games/update', newList);
  };

  const modificaCampo = (id, campo, valore) => {
    updateList(games.map(g => g.id === id ? { ...g, [campo]: valore } : g));
  };

  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    if (!backlog.length) return alert("Nessun gioco in backlog!");
    setIsSpinning(true);
    let count = 0;
    const interval = setInterval(() => {
      setSpinTitle(backlog[Math.floor(Math.random() * backlog.length)].titolo);
      if (++count > 20) {
        clearInterval(interval);
        const finale = backlog[Math.floor(Math.random() * backlog.length)];
        setChosenGame(finale);
        setHistory(prev => [finale.titolo, ...prev].slice(0, 5));
        setIsSpinning(false);
      }
    }, 100);
  };

  const pulisciNomeSaga = (s) => (s || "").replace(/Series|Saga/gi, "").trim();
  const dividi = (v) => String(v || "").split(',').map(i => i.trim()).filter(i => i);
  const colorStato = (s) => ({ 'Completato': '#27ae60', 'In corso': '#f1c40f', 'Droppato': '#e74c3c', 'Sospeso': '#e67e22' }[s] || '#7f8c8d');

  const filtered = games.filter(g => 
    g.titolo.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (filterYear === 'Tutti' || dividi(g.annoGiocato).includes(filterYear)) &&
    (selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(g.saga) : pulisciNomeSaga(g.saga) === selectedSaga))
  ).sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI' }}>
      <style>{`
        .card { background: #161616; border-radius: 12px; overflow: hidden; border: 1px solid #333; transition: 0.3s; position: relative; }
        .card:hover { transform: translateY(-5px); border-color: #00d1b2; }
        .mask { position: absolute; inset: 0; background: rgba(0,0,0,0.85); opacity: 0; transition: 0.3s; display: flex; flex-direction: column; align-items: center; justify-content: center; padding: 10px; z-index: 5; backdrop-filter: blur(5px); }
        .card:hover .mask { opacity: 1; }
        .chip { background: rgba(0,209,178,0.1); color: #00d1b2; padding: 3px 8px; border-radius: 4px; font-size: 11px; margin: 2px; }
        @media (max-width: 768px) { .sidebar { position: fixed; left: ${isMobileOpen ? '0' : '-100%'}; width: 80% !important; z-index: 100; } }
      `}</style>

      {/* Sidebar */}
      <div className="sidebar" style={{ width: isCollapsed ? '70px' : '260px', background: '#111', padding: '20px', borderRight: '1px solid #333', transition: '0.3s', overflowY: 'auto' }}>
        <button onClick={() => setIsCollapsed(!isCollapsed)} style={{ background: 'none', border: 'none', color: '#00d1b2', cursor: 'pointer', marginBottom: '20px' }}>{isCollapsed ? 'âž¡' : 'â¬…'}</button>
        {!isCollapsed && (
          <>
            <button onClick={avviaSpinner} style={{ width: '100%', padding: '12px', background: 'linear-gradient(45deg, #f1c40f, #e67e22)', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>ðŸŽ² COSA GIOCO?</button>
            {isAdmin && <button onClick={() => setShowAddForm(!showAddForm)} style={{ width: '100%', padding: '10px', background: '#00d1b2', border: 'none', borderRadius: '8px', marginBottom: '10px', fontWeight: 'bold' }}>{showAddForm ? 'CHIUDI' : '+ AGGIUNGI'}</button>}
            
            <div style={{ fontSize: '14px', color: '#888' }}>
              <div onClick={() => setSelectedSaga('Tutte')} style={{ padding: '8px', cursor: 'pointer', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }}>Tutte le Saghe</div>
              <div onClick={() => setSelectedSaga('Senza Saga')} style={{ padding: '8px', cursor: 'pointer', color: selectedSaga === 'Senza Saga' ? '#00d1b2' : '#888' }}>Senza Saga</div>
              {[...new Set(games.map(g => pulisciNomeSaga(g.saga)))].filter(s => s).sort().map(s => (
                <div key={s} onClick={() => setSelectedSaga(s)} style={{ padding: '4px 8px', cursor: 'pointer', color: selectedSaga === s ? '#00d1b2' : '#888' }}>{s}</div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
          <input placeholder="ðŸ” Cerca gioco..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '12px', background: '#161616', border: '1px solid #333', borderRadius: '8px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ padding: '12px', background: '#161616', color: '#00d1b2', border: '1px solid #333', borderRadius: '8px' }}>
            <option value="Tutti">ðŸ“… Tutti gli Anni</option>
            {[...new Set(games.flatMap(g => dividi(g.annoGiocato)))].sort((a,b) => b-a).map(a => <option key={a} value={a}>{a}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))', gap: '20px' }}>
          {filtered.map(game => (
            <div key={game.id} className="card">
              <div style={{ height: '300px', position: 'relative' }}>
                <img src={game.copertina} style={{ width: '100%', height: '100%', objectFit: 'cover' }} alt="" />
                <div className="mask">
                  {isAdmin ? (
                    <div style={{ width: '90%', display: 'flex', flexDirection: 'column', gap: '5px' }}>
                      <button onClick={() => updateList(games.filter(g => g.id !== game.id))} style={{ background: '#e74c3c', border: 'none', color: 'white', padding: '5px' }}>ELIMINA</button>
                      <input defaultValue={game.saga} onBlur={e => modificaCampo(game.id, 'saga', e.target.value)} placeholder="Saga" style={{ background: '#000', color: '#fff', border: '1px solid #444', padding: '4px' }} />
                      <input defaultValue={game.annoUscita} onBlur={e => modificaCampo(game.id, 'annoUscita', e.target.value)} placeholder="Uscita" style={{ background: '#000', color: '#fff', border: '1px solid #444', padding: '4px' }} />
                      <input defaultValue={game.annoGiocato} onBlur={e => modificaCampo(game.id, 'annoGiocato', e.target.value)} placeholder="Giocato" style={{ background: '#000', color: '#fff', border: '1px solid #444', padding: '4px' }} />
                    </div>
                  ) : (
                    <div style={{ textAlign: 'center' }}>
                      <h4 style={{ color: '#00d1b2', margin: '0 0 10px 0' }}>{pulisciNomeSaga(game.saga) || '-'}</h4>
                      <p style={{ margin: '2px 0', fontSize: '14px' }}>Uscita: <b>{game.annoUscita || game.anno || 'N/A'}</b></p>
                      <p style={{ margin: '2px 0', fontSize: '14px' }}>Giocato: <b>{game.annoGiocato || '---'}</b></p>
                      <div style={{ marginTop: '10px' }}>{dividi(game.piattaforma).map(p => <span key={p} className="chip">{p}</span>)}</div>
                    </div>
                  )}
                </div>
              </div>
              <div style={{ padding: '15px' }}>
                <div style={{ fontSize: '10px', fontWeight: 'bold', color: colorStato(game.stato), textTransform: 'uppercase' }}>{game.stato}</div>
                <div style={{ fontWeight: 'bold', marginTop: '5px' }}>{game.titolo}</div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal Spinner */}
      {(isSpinning || chosenGame) && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.95)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#1a1a1a', padding: '30px', borderRadius: '20px', border: '2px solid #f1c40f', textAlign: 'center', width: '90%', maxWidth: '400px' }}>
            {isSpinning ? (
              <h2 style={{ color: '#f1c40f' }}>{spinTitle}</h2>
            ) : (
              <div>
                <img src={chosenGame.copertina} style={{ width: '100%', borderRadius: '10px' }} alt="" />
                <h3>{chosenGame.titolo}</h3>
                <button onClick={() => setChosenGame(null)} style={{ background: '#f1c40f', border: 'none', padding: '10px 40px', borderRadius: '8px', fontWeight: 'bold' }}>DAJE!</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
