import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [loading, setLoading] = useState(true);

  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Database sincronizzato con l'Excel!");
    } catch (err) {
      alert("Errore nell'upload");
    } finally {
      setLoading(false);
    }
  };

  const salvaModificaSaga = async (id, nuovaSaga) => {
    const valorePulito = nuovaSaga.trim();
    const listaAggiornata = games.map(g => g.id === id ? { ...g, saga: valorePulito } : g);
    setGames(listaAggiornata);
    
    try {
      await axios.post('http://localhost:5000/api/games/update', listaAggiornata);
    } catch (err) {
      alert("Errore durante il salvataggio");
    }
  };

  // --- LOGICA FILTRI PULITA ---
  // Prendiamo solo le saghe che hanno effettivamente un nome scritto
  const saghePopolate = [...new Set(games.map(g => g.saga))].filter(s => s && s.trim() !== "");
  const saghePerSidebar = ['Tutte', 'Senza Saga', ...saghePopolate];

  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    
    let matchesSaga = false;
    if (selectedSaga === 'Tutte') matchesSaga = true;
    else if (selectedSaga === 'Senza Saga') matchesSaga = !game.saga || game.saga.trim() === "";
    else matchesSaga = game.saga === selectedSaga;

    return matchesSearch && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#121212', color: 'white', fontFamily: 'Segoe UI' }}>
      
      {/* Sidebar */}
      <div style={{ width: '250px', padding: '20px', borderRight: '1px solid #333', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto', backgroundColor: '#181818' }}>
        <h3 style={{ borderBottom: '1px solid #333', paddingBottom: '10px', color: '#00d1b2' }}>Esplora</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {saghePerSidebar.map(s => (
            <li 
              key={s} 
              onClick={() => setSelectedSaga(s)} 
              style={{ 
                cursor: 'pointer', 
                padding: '10px 8px', 
                borderRadius: '4px', 
                marginBottom: '4px', 
                backgroundColor: selectedSaga === s ? '#00d1b222' : 'transparent', 
                color: selectedSaga === s ? '#00d1b2' : '#bbb',
                fontSize: '14px'
              }}
            >
              {s}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', gap: '20px' }}>
          <input 
            type="text" 
            placeholder="Cerca un gioco..." 
            onChange={(e) => setSearchTerm(e.target.value)} 
            style={{ flex: 1, padding: '15px', background: '#222', color: 'white', border: '1px solid #444', borderRadius: '8px', outline: 'none' }} 
          />
          <div style={{ backgroundColor: '#222', padding: '10px', borderRadius: '8px', border: '1px solid #444' }}>
             <input type="file" accept=".xlsx" onChange={handleFileUpload} style={{ fontSize: '12px' }} />
          </div>
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', color: '#00d1b2', marginTop: '50px' }}>Caricamento database...</div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))', gap: '25px' }}>
            {filteredGames.map((game) => (
              <div key={game.id} style={{ background: '#1e1e1e', borderRadius: '12px', overflow: 'hidden', border: '1px solid #333', transition: '0.3s' }}>
                <img 
                  src={game.copertina} 
                  alt={game.titolo} 
                  style={{ width: '100%', height: '280px', objectFit: 'cover' }} 
                  onError={(e) => { e.target.src = 'https://via.placeholder.com/200x280?text=No+Cover'; }} 
                />
                <div style={{ padding: '15px' }}>
                  <h4 style={{ margin: '0 0 5px 0', fontSize: '15px', color: '#fff' }}>{game.titolo}</h4>
                  <small style={{ color: '#00d1b2' }}>{game.piattaforma}</small>
                  
                  {/* INPUT EDITABILE SAGA */}
                  <div style={{ marginTop: '12px', borderTop: '1px solid #333', paddingTop: '10px' }}>
                    <label style={{ fontSize: '9px', color: '#666', display: 'block', textTransform: 'uppercase' }}>Saga</label>
                    <input 
                      type="text"
                      defaultValue={game.saga}
                      placeholder="Nessuna saga..."
                      onBlur={(e) => salvaModificaSaga(game.id, e.target.value)}
                      style={{ 
                        background: 'transparent', 
                        border: 'none', 
                        color: game.saga ? '#00d1b2' : '#444', 
                        fontSize: '13px', 
                        width: '100%', 
                        outline: 'none',
                        fontStyle: game.saga ? 'normal' : 'italic',
                        marginTop: '2px'
                      }}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {!loading && filteredGames.length === 0 && (
          <div style={{ textAlign: 'center', marginTop: '100px', color: '#555' }}>Nessun gioco trovato.</div>
        )}
      </div>
    </div>
  );
};

export default App;
