import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [showAddForm, setShowAddForm] = useState(false);
  
  // Stati per la funzione Spinner
  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');

  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato'
  });

  // ... (Funzioni dividiStringa, getColorStato, fetchGames, pulisciNomeSaga, modificaCampo, aggiungiGioco, eliminaGioco invariate)
  const dividiStringa = (valore) => {
    if (!valore) return [];
    return String(valore).split(',').map(item => item.trim()).filter(item => item !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60';
      case 'In corso': return '#f1c40f';
      case 'Droppato': return '#e74c3c';
      case 'Sospeso': return '#e67e22';
      default: return '#7f8c8d';
    }
  };

  const fetchGames = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const modificaCampo = async (id, campo, valore) => {
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    await axios.post('http://localhost:5000/api/games/update', nuovaLista);
  };

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const nuovoGiocoConId = { ...newGame, id: Date.now() };
    const nuovaLista = [...games, nuovoGiocoConId];
    setGames(nuovaLista);
    await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato' });
    setShowAddForm(false);
  };

  const eliminaGioco = async (id, titolo) => {
    const conferma = window.confirm(`Sei sicuro di voler eliminare definitivamente "${titolo}" dalla tua lista?`);
    if (conferma) {
      const nuovaLista = games.filter(g => g.id !== id);
      setGames(nuovaLista);
      await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
  };

  // --- FUNZIONE SPINNER ---
  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    
    if (backlog.length === 0) {
      alert("Non hai giochi 'Non Giocati' in lista!");
      return;
    }

    setIsSpinning(true);
    setChosenGame(null);

    let giri = 0;
    const intervallo = setInterval(() => {
      const randomGame = backlog[Math.floor(Math.random() * backlog.length)];
      setSpinTitle(randomGame.titolo);
      giri++;

      if (giri > 20) {
        clearInterval(intervallo);
        setChosenGame(randomGame);
        setIsSpinning(false);
      }
    }, 100);
  };

  const anniPerFiltro = [...new Set(games.flatMap(g => dividiStringa(g.annoGiocato)))].sort((a, b) => b - a);

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesYear = filterYear === 'Tutti' || dividiStringa(game.annoGiocato).includes(filterYear);
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      return matchesSearch && matchesYear && matchesSaga;
    })
    .sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', minHeight: '100vh', width: '100%', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', margin: 0 }}>
      
      <style>{`
        body { margin: 0; padding: 0; background-color: #0a0a0a; overflow: hidden; }
        .game-card { position: relative; border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(10px); }
        .game-card:hover .info-mask { opacity: 1; }
        .platform-chip { background: rgba(0, 209, 178, 0.15); color: #00d1b2; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; }
        .year-chip { background: #252525; color: #fff; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; border: 1px solid #444; }
        .status-badge { font-size: 10px; text-transform: uppercase; font-weight: 900; padding: 2px 8px; border-radius: 4px; display: inline-block; margin-bottom: 8px; }
        
        /* Modal Stili */
        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.9); z-index: 100; display: flex; align-items: center; justify-content: center; padding: 20px; }
        .modal-content { background: #1a1a1a; padding: 30px; border-radius: 20px; border: 2px solid #f1c40f; text-align: center; max-width: 400px; width: 100%; box-shadow: 0 0 30px rgba(241, 196, 15, 0.3); }
        
        .btn-spin { background: linear-gradient(45deg, #f1c40f, #e67e22); color: black; border: none; padding: 12px; border-radius: 8px; font-weight: bold; cursor: pointer; width: 100%; margin-bottom: 20px; transition: 0.3s; }
        .btn-spin:hover { transform: scale(1.02); box-shadow: 0 0 15px rgba(241, 196, 15, 0.4); }
      `}</style>

      {/* MODAL RISULTATO SPINNER */}
      {(isSpinning || chosenGame) && (
        <div className="modal-overlay">
          <div className="modal-content">
            {isSpinning ? (
              <>
                <h2 style={{ color: '#f1c40f' }}>Scegliendo...</h2>
                <div style={{ fontSize: '24px', fontWeight: 'bold', margin: '20px 0' }}>{spinTitle}</div>
              </>
            ) : (
              <>
                <h2 style={{ color: '#f1c40f' }}>DEVI GIOCARE A:</h2>
                <img src={chosenGame.copertina} style={{ width: '200px', borderRadius: '10px', marginBottom: '20px' }} alt="" />
                <h1 style={{ margin: '10px 0' }}>{chosenGame.titolo}</h1>
                <button onClick={() => setChosenGame(null)} style={{ background: '#333', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer' }}>Chiudi</button>
              </>
            )}
          </div>
        </div>
      )}

      {/* Sidebar */}
      <div style={{ width: '260px', padding: '20px', borderRight: '1px solid #333', background: '#111', flexShrink: 0, overflowY: 'auto' }}>
        <h2 style={{ color: '#00d1b2' }}>ðŸŽ® My Games</h2>
        
        {/* Tasto Spinner */}
        <button className="btn-spin" onClick={avviaSpinner}>ðŸŽ² COSA GIOCO?</button>

        <button onClick={() => setShowAddForm(!showAddForm)} style={{ width: '100%', padding: '12px', background: '#00d1b2', border: 'none', borderRadius: '8px', color: '#000', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>
          {showAddForm ? 'Annulla' : '+ AGGIUNGI GIOCO'}
        </button>

        {showAddForm && (
          <form onSubmit={aggiungiGioco} style={{ background: '#1a1a1a', padding: '15px', borderRadius: '8px', marginBottom: '20px', border: '1px solid #00d1b2' }}>
            <input placeholder="Titolo" required style={{ width: '100%', padding: '8px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white' }} value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
            <input placeholder="Copertina" required style={{ width: '100%', padding: '8px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white' }} value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
            <input placeholder="Saga" style={{ width: '100%', padding: '8px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white' }} value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
            <button type="submit" style={{ width: '100%', padding: '10px', background: '#2ecc71', border: 'none', borderRadius: '6px', color: 'white' }}>SALVA</button>
          </form>
        )}

        <div style={{ marginTop: '20px' }}>
          <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte le Saghe</div>
          {[...new Set(games.map(g => pulisciNomeSaga(g.saga)))].filter(s => s).sort().map(s => (
            <div key={s} style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === s ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga(s)}>{s}</div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
          <input type="text" placeholder="Cerca titolo..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '12px', background: '#1a1a1a', border: '1px solid #333', borderRadius: '8px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ padding: '12px', background: '#1a1a1a', color: '#00d1b2', borderRadius: '8px', border: '1px solid #333' }}>
            <option value="Tutti">Anno: Tutti</option>
            {anniPerFiltro.map(a => <option key={a} value={a}>{a}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '25px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div style={{ position: 'relative', height: '340px', background: '#000' }}>
                <img src={game.copertina} style={{ width: '100%', height: '100%', objectFit: 'contain' }} alt={game.titolo} />
                <div className="info-mask">
                  <button onClick={() => eliminaGioco(game.id, game.titolo)} style={{ position: 'absolute', top: 10, left: 10, background: '#e74c3c', border: 'none', color: 'white', padding: '5px' }}>ðŸ—‘</button>
                  <select 
                    style={{ background: getColorStato(game.stato), color: 'white', padding: '8px', border: 'none', borderRadius: '5px' }}
                    value={game.stato}
                    onChange={e => modificaCampo(game.id, 'stato', e.target.value)}
                  >
                    {['Non Giocato', 'Completato', 'In corso', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                  </select>
                </div>
              </div>
              <div style={{ padding: '15px' }}>
                <div className="status-badge" style={{ background: getColorStato(game.stato) }}>{game.stato}</div>
                <h4 style={{ margin: 0 }}>{game.titolo}</h4>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
