import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [loading, setLoading] = useState(true);

  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Database sincronizzato con l'Excel!");
    } catch (err) {
      alert("Errore nell'upload");
    } finally {
      setLoading(false);
    }
  };

  // Funzione universale per salvare qualsiasi modifica al database
  const updateGameData = async (updatedList) => {
    try {
      await axios.post('http://localhost:5000/api/games/update', updatedList);
    } catch (err) {
      console.error("Errore durante il salvataggio");
    }
  };

  const salvaModificaSaga = (id, nuovaSaga) => {
    const valorePulito = nuovaSaga.trim();
    const listaAggiornata = games.map(g => g.id === id ? { ...g, saga: valorePulito } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const cambiaStato = (id, nuovoStato) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, stato: nuovoStato } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  // Mappa dei colori per gli stati
  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60'; // Verde
      case 'In corso': return '#f1c40f';    // Giallo
      case 'Droppato': return '#e74c3c';    // Rosso
      case 'Sospeso': return '#e67e22';     // Arancione
      default: return '#444';               // Grigio (Non Giocato)
    }
  };

  const saghePopolate = [...new Set(games.map(g => g.saga))].filter(s => s && s.trim() !== "").sort();
  const saghePerSidebar = ['Tutte', 'Senza Saga', ...saghePopolate];

  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    let matchesSaga = false;
    if (selectedSaga === 'Tutte') matchesSaga = true;
    else if (selectedSaga === 'Senza Saga') matchesSaga = !game.saga || game.saga.trim() === "";
    else matchesSaga = game.saga === selectedSaga;
    return matchesSearch && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#121212', color: 'white', fontFamily: 'Segoe UI' }}>
      
      {/* CSS per l'effetto Hover */}
      <style>{`
        .game-card { position: relative; transition: transform 0.2s; }
        .game-card:hover { transform: translateY(-5px); }
        .status-mask {
          position: absolute;
          right: -50px; top: 0; bottom: 0; width: 50px;
          transition: right 0.3s ease;
          display: flex; flex-direction: column; align-items: center; justify-content: space-around;
          padding: 10px 0; z-index: 10; border-left: 1px solid rgba(255,255,255,0.1);
        }
        .game-card:hover .status-mask { right: 0; }
        .date-text {
          writing-mode: vertical-rl; transform: rotate(180deg);
          font-size: 11px; font-weight: bold; color: rgba(255,255,255,0.9);
          letter-spacing: 1px;
        }
        .status-select {
          background: transparent; border: 1px solid rgba(255,255,255,0.5);
          color: white; border-radius: 4px; cursor: pointer; font-size: 14px;
        }
        .status-select option { background: #222; color: white; }
      `}</style>

      <datalist id="saghe-list">
        {saghePopolate.map((s, index) => (
          <option key={index} value={s} />
        ))}
      </datalist>

      {/* Sidebar */}
      <div style={{ width: '250px', padding: '20px', borderRight: '1px solid #333', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto', backgroundColor: '#181818' }}>
        <h3 style={{ borderBottom: '1px solid #333', paddingBottom: '10px', color: '#00d1b2' }}>Esplora</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {saghePerSidebar.map(s => (
            <li 
              key={s} 
              onClick={() => setSelectedSaga(s)} 
              style={{ 
                cursor: 'pointer', padding: '10px 8px', borderRadius: '4px', marginBottom: '4px', 
                backgroundColor: selectedSaga === s ? '#00d1b222' : 'transparent', 
                color: selectedSaga === s ? '#00d1b2' : '#bbb', fontSize: '14px'
              }}
            >
              {s}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', gap: '20px' }}>
          <input 
            type="text" placeholder="Cerca un gioco..." 
            onChange={(e) => setSearchTerm(e.target.value)} 
            style={{ flex: 1, padding: '15px', background: '#222', color: 'white', border: '1px solid #444', borderRadius: '8px', outline: 'none' }} 
          />
          <div style={{ backgroundColor: '#222', padding: '10px', borderRadius: '8px', border: '1px solid #444' }}>
             <input type="file" accept=".xlsx" onChange={handleFileUpload} style={{ fontSize: '12px' }} />
          </div>
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', color: '#00d1b2', marginTop: '50px' }}>Caricamento database...</div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))', gap: '25px' }}>
            {filteredGames.map((game) => (
              <div key={game.id} className="game-card" style={{ background: '#1e1e1e', borderRadius: '12px', overflow: 'hidden', border: '1px solid #333' }}>
                
                {/* MASCHERA LATERALE */}
                <div className="status-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                  <div className="date-text">
                    {game.annoGiocato && game.annoGiocato !== "-" ? game.annoGiocato : "NON GIOCATO"}
                  </div>
                  <select 
                    className="status-select"
                    value={game.stato || 'Non Giocato'}
                    onChange={(e) => cambiaStato(game.id, e.target.value)}
                  >
                    <option value="Non Giocato">âšª</option>
                    <option value="Completato">ðŸŸ¢</option>
                    <option value="In corso">ðŸŸ¡</option>
                    <option value="Sospeso">ðŸŸ </option>
                    <option value="Droppato">ðŸ”´</option>
                  </select>
                </div>

                <img 
                  src={game.copertina} alt={game.titolo} 
                  style={{ width: '100%', height: '280px', objectFit: 'cover' }} 
                  onError={(e) => { e.target.src = 'https://via.placeholder.com/200x280?text=No+Cover'; }} 
                />
                
                <div style={{ padding: '15px' }}>
                  <h4 style={{ margin: '0 0 5px 0', fontSize: '15px', color: '#fff' }}>{game.titolo}</h4>
                  <small style={{ color: '#00d1b2' }}>{game.piattaforma}</small>
                  
                  <div style={{ marginTop: '12px', borderTop: '1px solid #333', paddingTop: '10px' }}>
                    <label style={{ fontSize: '9px', color: '#666', display: 'block', textTransform: 'uppercase' }}>Saga</label>
                    <input 
                      type="text" defaultValue={game.saga} placeholder="Nessuna saga..." list="saghe-list"
                      onBlur={(e) => salvaModificaSaga(game.id, e.target.value)}
                      style={{ 
                        background: 'transparent', border: 'none', color: game.saga ? '#00d1b2' : '#444', 
                        fontSize: '13px', width: '100%', outline: 'none', fontStyle: game.saga ? 'normal' : 'italic', marginTop: '2px'
                      }}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
