import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [showAddForm, setShowAddForm] = useState(false);

  const [newGame, setNewGame] = useState({
    titolo: '',
    copertina: '',
    saga: '',
    annoUscita: '',
    annoGiocato: '',
    piattaforma: '',
    stato: 'Non Giocato'
  });

  const dividiStringa = (valore) => {
    if (!valore) return [];
    return String(valore).split(',').map(item => item.trim()).filter(item => item !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60';
      case 'In corso': return '#f1c40f';
      case 'Droppato': return '#e74c3c';
      case 'Sospeso': return '#e67e22';
      default: return '#7f8c8d';
    }
  };

  const fetchGames = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const modificaCampo = async (id, campo, valore) => {
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    await axios.post('http://localhost:5000/api/games/update', nuovaLista);
  };

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const nuovoGiocoConId = { ...newGame, id: Date.now() };
    const nuovaLista = [...games, nuovoGiocoConId];
    setGames(nuovaLista);
    await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato' });
    setShowAddForm(false);
  };

  const eliminaGioco = async (id, titolo) => {
    const conferma = window.confirm(`Sei sicuro di voler eliminare definitivamente "${titolo}" dalla tua lista?`);
    if (conferma) {
      const nuovaLista = games.filter(g => g.id !== id);
      setGames(nuovaLista);
      await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
  };

  const anniPerFiltro = [...new Set(games.flatMap(g => dividiStringa(g.annoGiocato)))].sort((a, b) => b - a);

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesYear = filterYear === 'Tutti' || dividiStringa(game.annoGiocato).includes(filterYear);
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      return matchesSearch && matchesYear && matchesSaga;
    })
    .sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', minHeight: '100vh', width: '100%', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', margin: 0 }}>
      
      <style>{`
        body { margin: 0; padding: 0; background-color: #0a0a0a; overflow-x: hidden; }
        .game-card { position: relative; border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; }
        .card-rigiocato { border: 1px solid #f1c40f !important; }
        .platform-chip { background: rgba(0, 209, 178, 0.15); color: #00d1b2; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; }
        .year-chip { background: #252525; color: #fff; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; border: 1px solid #444; }
        .status-badge { font-size: 10px; text-transform: uppercase; font-weight: 900; padding: 2px 8px; border-radius: 4px; display: inline-block; margin-bottom: 8px; }
        .replay-badge { position: absolute; top: 10px; right: 10px; background: #f1c40f; color: #000; width: 28px; height: 28px; border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 16px; z-index: 10; box-shadow: 0 0 10px rgba(0,0,0,0.5); }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(10px); }
        .game-card:hover .info-mask { opacity: 1; }
        .add-input { width: 100%; padding: 10px; margin-bottom: 10px; background: #222; border: 1px solid #444; color: white; border-radius: 6px; box-sizing: border-box; }
        .btn-delete { position: absolute; top: 10px; left: 10px; background: rgba(231, 76, 60, 0.8); color: white; border: none; border-radius: 4px; padding: 5px 8px; cursor: pointer; font-size: 12px; transition: 0.2s; }
        .btn-delete:hover { background: #e74c3c; transform: scale(1.1); }
      `}</style>

      {/* Sidebar */}
      <div style={{ width: '260px', padding: '20px', borderRight: '1px solid #333', background: '#111', flexShrink: 0 }}>
        <h2 style={{ color: '#00d1b2' }}>ðŸŽ® La mia Lista</h2>
        
        <button onClick={() => setShowAddForm(!showAddForm)} style={{ width: '100%', padding: '12px', background: '#00d1b2', border: 'none', borderRadius: '8px', color: '#000', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>
          {showAddForm ? 'Annulla' : '+ AGGIUNGI GIOCO'}
        </button>

        {showAddForm && (
          <form onSubmit={aggiungiGioco} style={{ background: '#1a1a1a', padding: '15px', borderRadius: '8px', marginBottom: '20px', border: '1px solid #00d1b2' }}>
            <input placeholder="Titolo" required className="add-input" value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
            <input placeholder="URL Copertina" required className="add-input" value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
            <input placeholder="Saga" className="add-input" value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
            <input placeholder="Piattaforme (es: PC, PS5)" className="add-input" value={newGame.piattaforma} onChange={e => setNewGame({...newGame, piattaforma: e.target.value})} />
            <button type="submit" style={{ width: '100%', padding: '10px', background: '#2ecc71', border: 'none', borderRadius: '6px', color: 'white', fontWeight: 'bold', cursor: 'pointer' }}>SALVA</button>
          </form>
        )}

        <div style={{ marginTop: '20px' }}>
          <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte le Saghe</div>
          {[...new Set(games.map(g => pulisciNomeSaga(g.saga)))].filter(s => s).sort().map(s => (
            <div key={s} style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === s ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga(s)}>{s}</div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
          <input type="text" placeholder="Cerca titolo..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '12px', background: '#1a1a1a', border: '1px solid #333', borderRadius: '8px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ padding: '12px', background: '#1a1a1a', color: '#00d1b2', borderRadius: '8px', border: '1px solid #333' }}>
            <option value="Tutti">Anno: Tutti</option>
            {anniPerFiltro.map(a => <option key={a} value={a}>{a}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '25px' }}>
          {filteredGames.map(game => {
            const piattaforme = dividiStringa(game.piattaforma);
            const anniGiocati = dividiStringa(game.annoGiocato);
            const isRigiocato = anniGiocati.length > 1;

            return (
              <div key={game.id} className={`game-card ${isRigiocato ? 'card-rigiocato' : ''}`}>
                {isRigiocato && <div className="replay-badge">ðŸ”„</div>}
                
                <div style={{ position: 'relative', height: '340px', background: '#000', overflow: 'hidden' }}>
                  <img src={game.copertina} style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover', filter: 'blur(8px) opacity(0.3)' }} alt="" />
                  <img src={game.copertina} style={{ position: 'relative', width: '100%', height: '100%', objectFit: 'contain', zIndex: 1 }} alt={game.titolo} />
                  
                  <div className="info-mask" style={{ backgroundColor: 'rgba(0,0,0,0.8)' }}>
                    <button className="btn-delete" onClick={() => eliminaGioco(game.id, game.titolo)}>ðŸ—‘ Rimuovi</button>
                    
                    <select 
                      style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', background: getColorStato(game.stato), color: 'white', border: 'none', fontWeight: 'bold' }}
                      value={game.stato}
                      onChange={e => modificaCampo(game.id, 'stato', e.target.value)}
                    >
                      {['Non Giocato', 'Completato', 'In corso', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>

                    <span style={{ fontSize: '10px', color: '#aaa' }}>SAGA</span>
                    <input style={{ background: '#000', border: '1px solid #444', color: 'white', padding: '5px', textAlign: 'center', width: '80%', marginBottom: '10px' }} defaultValue={game.saga} onBlur={e => modificaCampo(game.id, 'saga', e.target.value)} />

                    <span style={{ fontSize: '10px', color: '#aaa' }}>ANNI (usa virgola)</span>
                    <input style={{ background: '#000', border: '1px solid #444', color: 'white', padding: '5px', textAlign: 'center', width: '80%', marginBottom: '10px' }} defaultValue={game.annoGiocato} onBlur={e => modificaCampo(game.id, 'annoGiocato', e.target.value)} />
                    
                    <span style={{ fontSize: '10px', color: '#aaa' }}>PIATTAFORME</span>
                    <input style={{ background: '#000', border: '1px solid #444', color: 'white', padding: '5px', textAlign: 'center', width: '80%' }} defaultValue={game.piattaforma} onBlur={e => modificaCampo(game.id, 'piattaforma', e.target.value)} />
                  </div>
                </div>

                <div style={{ padding: '15px' }}>
                  {game.saga && (
                    <div style={{ fontSize: '10px', color: '#666', textTransform: 'uppercase', letterSpacing: '1px', fontWeight: 'bold', marginBottom: '4px' }}>
                      {pulisciNomeSaga(game.saga)}
                    </div>
                  )}
                  <div className="status-badge" style={{ backgroundColor: getColorStato(game.stato), color: game.stato === 'In corso' ? '#000' : '#fff' }}>
                    {game.stato}
                  </div>
                  <h4 style={{ margin: '0 0 12px 0', fontSize: '18px', lineHeight: '1.2' }}>{game.titolo}</h4>
                  <div>{piattaforme.map((p, i) => <span key={i} className="platform-chip">{p}</span>)}</div>
                  <div style={{ marginTop: '5px' }}>{anniGiocati.map((a, i) => <span key={i} className="year-chip">ðŸ“… {a}</span>)}</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default App;
