import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti'); // NUOVO STATO PER FILTRO ANNO
  const [sortType, setSortType] = useState('titolo'); 
  const [loading, setLoading] = useState(true);

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    const eccezioni = ["Remedy Connected Universe"];
    if (eccezioni.includes(saga.trim())) return saga.trim();
    return saga.replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchGames(); }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Database sincronizzato!");
    } catch (err) { alert("Errore nell'upload"); } finally { setLoading(false); }
  };

  const updateGameData = async (updatedList) => {
    try { await axios.post('http://localhost:5000/api/games/update', updatedList); } 
    catch (err) { console.error("Errore salvataggio"); }
  };

  const modificaCampo = (id, campo, valore) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, [campo]: valore.trim() } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const cambiaStato = (id, nuovoStato) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, stato: nuovoStato } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return 'rgba(39, 174, 96, 0.85)';
      case 'In corso': return 'rgba(241, 196, 15, 0.85)';
      case 'Droppato': return 'rgba(231, 76, 60, 0.85)';
      case 'Sospeso': return 'rgba(230, 126, 34, 0.85)';
      default: return 'rgba(40, 40, 40, 0.85)';
    }
  };

  // Estrarre lista unica di anni di gioco per il filtro (escludendo "-" o vuoti)
  const anniDisponibili = [...new Set(games.map(g => g.annoGiocato))]
    .filter(a => a && a !== "-" && a.trim() !== "")
    .sort((a, b) => b - a);

  const saghePopolate = [...new Set(games.map(g => pulisciNomeSaga(g.saga)))]
    .filter(s => s && s !== "").sort();
  const saghePerSidebar = ['Tutte', 'Senza Saga', ...saghePopolate];

  // LOGICA DI FILTRO E ORDINAMENTO
  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const sagaVisualizzata = pulisciNomeSaga(game.saga);
      
      let matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !sagaVisualizzata : sagaVisualizzata === selectedSaga);
      
      // NUOVO: Filtro per Anno di Gioco
      let matchesYear = filterYear === 'Tutti' || game.annoGiocato === filterYear;
      
      return matchesSearch && matchesSaga && matchesYear;
    })
    .sort((a, b) => {
      const convertiAnno = (val) => {
        const n = parseInt(val, 10);
        return isNaN(n) ? 0 : n;
      };

      switch (sortType) {
        case 'titolo': return a.titolo.localeCompare(b.titolo);
        case 'uscita-desc': return convertiAnno(b.annoUscita) - convertiAnno(a.annoUscita);
        case 'uscita-asc': return convertiAnno(a.annoUscita) - convertiAnno(b.annoUscita);
        case 'giocato-desc': return convertiAnno(b.annoGiocato) - convertiAnno(a.annoGiocato);
        case 'giocato-asc': return convertiAnno(a.annoGiocato) - convertiAnno(b.annoGiocato);
        default: return 0;
      }
    });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI' }}>
      
      <style>{`
        .game-card { position: relative; overflow: hidden; border-radius: 12px; background: #181818; border: 1px solid #333; display: flex; flex-direction: column; transition: transform 0.2s; }
        .game-card:hover { transform: translateY(-3px); }
        .image-container { position: relative; width: 100%; height: 320px; background: #000; overflow: hidden; display: flex; align-items: center; justify-content: center; }
        .bg-blur { position: absolute; width: 100%; height: 100%; object-fit: cover; filter: blur(15px) brightness(0.5); transform: scale(1.1); z-index: 0; }
        .game-img { position: relative; max-width: 100%; max-height: 100%; object-fit: contain; z-index: 1; display: block; }
        .info-mask { position: absolute; top: 0; left: 0; right: 0; bottom: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: all 0.3s ease; z-index: 5; text-align: center; padding: 20px; backdrop-filter: blur(6px); }
        .game-card:hover .info-mask { opacity: 1; }
        .mask-label { font-size: 10px; text-transform: uppercase; letter-spacing: 2px; margin-bottom: 5px; color: rgba(255,255,255,0.9); }
        .mask-input-anno { background: transparent; border: none; color: white; font-size: 22px; font-weight: 800; text-align: center; width: 100%; margin-bottom: 25px; outline: none; }
        .status-select-large { background: white; border: none; color: #222; padding: 12px; border-radius: 8px; cursor: pointer; font-size: 14px; width: 85%; font-weight: bold; outline: none; text-align: center; text-align-last: center; }
        .inline-edit { background: transparent; border: none; color: inherit; font-family: inherit; font-size: inherit; font-weight: inherit; padding: 0; width: 100%; outline: none; transition: 0.2s; border-radius: 4px; }
        .inline-edit:focus { background: rgba(0, 209, 178, 0.1); color: #00d1b2; padding: 0 5px; }
        .platform-label { font-size: 11px; color: #00d1b2; font-weight: bold; text-transform: uppercase; margin-bottom: 5px; }
        
        /* Stile per le Select nella Toolbar */
        .toolbar-select { padding: 15px; background: #1a1a1a; color: #00d1b2; border: 1px solid #333; border-radius: 12px; outline: none; cursor: pointer; fontWeight: bold; }
      `}</style>

      {/* Sidebar */}
      <div style={{ width: '260px', padding: '25px', borderRight: '1px solid #333', backgroundColor: '#111', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto' }}>
        <h2 style={{ color: '#00d1b2', fontSize: '1.2rem', marginBottom: '20px' }}>La Mia Libreria</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {saghePerSidebar.map(s => (
            <li key={s} onClick={() => setSelectedSaga(s)} style={{ cursor: 'pointer', padding: '12px 15px', borderRadius: '8px', backgroundColor: selectedSaga === s ? '#00d1b215' : 'transparent', color: selectedSaga === s ? '#00d1b2' : '#888', transition: '0.2s', marginBottom: '5px' }}>{s}</li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '40px', backgroundColor: '#0f0f0f' }}>
        
        <div style={{ marginBottom: '40px', display: 'flex', flexWrap: 'wrap', gap: '20px', alignItems: 'center' }}>
          <input type="text" placeholder="Cerca un titolo..." onChange={(e) => setSearchTerm(e.target.value)} style={{ flex: 1, minWidth: '200px', padding: '18px', background: '#1a1a1a', color: 'white', border: '1px solid #333', borderRadius: '12px', fontSize: '16px', outline: 'none' }} />
          
          {/* NUOVA SELECT: FILTRO PER ANNO DI GIOCO */}
          <select value={filterYear} onChange={(e) => setFilterYear(e.target.value)} className="toolbar-select">
            <option value="Tutti">Giocato: Tutti gli anni</option>
            {anniDisponibili.map(anno => (
              <option key={anno} value={anno}>Giocato nel {anno}</option>
            ))}
          </select>

          <select value={sortType} onChange={(e) => setSortType(e.target.value)} className="toolbar-select">
            <option value="titolo">Ordina: A-Z</option>
            <option value="uscita-desc">Uscita: Più recenti ↓</option>
            <option value="uscita-asc">Uscita: Più vecchi ↑</option>
            <option value="giocato-desc">Giocato: Più recenti ↓</option>
            <option value="giocato-asc">Giocato: Più vecchi ↑</option>
          </select>

          <div style={{ display: 'flex', alignItems: 'center', background: '#1a1a1a', padding: '10px 15px', borderRadius: '12px', border: '1px solid #333' }}>
            <input type="file" accept=".xlsx" onChange={handleFileUpload} style={{ fontSize: '13px', color: '#888' }} />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map((game) => (
            <div key={game.id} className="game-card">
              {/* Contenuto della Card rimane identico */}
              <div className="image-container">
                <img className="bg-blur" src={game.copertina} alt="" />
                <div className="info-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                  <div className="mask-label">Giocato nel</div>
                  <input className="mask-input-anno" type="text" defaultValue={game.annoGiocato || ""} placeholder="---" onBlur={(e) => modificaCampo(game.id, 'annoGiocato', e.target.value)} />
                  <div className="mask-label">Stato Attuale</div>
                  <select className="status-select-large" value={game.stato || 'Non Giocato'} onChange={(e) => cambiaStato(game.id, e.target.value)}>
                    <option value="Non Giocato">Non Giocato</option>
                    <option value="Completato">Completato</option>
                    <option value="In corso">In corso</option>
                    <option value="Sospeso">Sospeso</option>
                    <option value="Droppato">Droppato</option>
                  </select>
                </div>
                <img className="game-img" src={game.copertina} alt={game.titolo} onError={(e) => { e.target.src = 'https://via.placeholder.com/260x320?text=No+Cover'; }} />
              </div>
              
              <div style={{ padding: '20px', flexGrow: 1, display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                <div>
                  <h4 style={{ margin: '0 0 5px 0', fontSize: '17px', fontWeight: '600' }}>{game.titolo}</h4>
                  <div style={{ fontSize: '12px', color: '#888', marginBottom: '8px' }}>
                    Uscita: <input className="inline-edit" style={{ width: '45px', color: '#bbb' }} type="text" defaultValue={game.annoUscita || ""} onBlur={(e) => modificaCampo(game.id, 'annoUscita', e.target.value)} />
                  </div>
                  <div className="platform-label">
                    <input className="inline-edit" type="text" defaultValue={game.piattaforma || "Piattaforma"} onBlur={(e) => modificaCampo(game.id, 'piattaforma', e.target.value)} />
                  </div>
                </div>
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #2a2a2a' }}>
                  <span style={{ fontSize: '10px', color: '#555', display: 'block', marginBottom: '5px' }}>COLLEZIONE / SAGA</span>
                  <input type="text" defaultValue={pulisciNomeSaga(game.saga)} placeholder="Saga..." onBlur={(e) => modificaCampo(game.id, 'saga', e.target.value)}
                    style={{ background: 'transparent', border: 'none', color: '#bbb', fontSize: '13px', width: '100%', outline: 'none', fontStyle: 'italic' }}
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
