import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [filterStatus, setFilterStatus] = useState('Tutti');
  const [showAddModal, setShowAddModal] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [notification, setNotification] = useState({ show: false, message: '' });
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);

  // Spinner State
  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => JSON.parse(localStorage.getItem('spinHistory') || '[]'));

  // Nuovo gioco state
  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: ''
  });

  // Accesso Admin
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey && (e.key === 'A' || e.key === 'a')) {
        const pass = prompt("Accesso protetto:");
        if (pass === "listone") setIsAdmin(true);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  useEffect(() => { localStorage.setItem('spinHistory', JSON.stringify(history)); }, [history]);

  const fetchGames = async () => {
    try {
      const url = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/games' : '/games.json';
      const res = await axios.get(url);
      setGames(res.data);
    } catch (err) { console.error(err); }
  };
  useEffect(() => { fetchGames(); }, []);

  const showToast = (msg) => {
    setNotification({ show: true, message: msg });
    setTimeout(() => setNotification({ show: false, message: '' }), 3000);
  };

  const dividiStringa = (v) => v ? String(v).split(',').map(i => i.trim()).filter(i => i !== "") : [];
  const getColorStato = (s) => ({ 'Completato': '#27ae60', 'In corso': '#f1c40f', 'Droppato': '#e74c3c', 'Sospeso': '#e67e22' }[s] || '#7f8c8d');
  const pulisciNomeSaga = (s) => s ? String(s).replace(/Series/gi, "").replace(/Saga/gi, "").trim() : "";

  // CRUD
  const modificaCampo = async (id, campo, valore) => {
    if (!isAdmin) return;
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') await axios.post('http://localhost:5000/api/games/update', nuovaLista);
  };

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const lista = [...games, { ...newGame, id: Date.now() }];
    setGames(lista);
    if (window.location.hostname === 'localhost') await axios.post('http://localhost:5000/api/games/update', lista);
    showToast(`"${newGame.titolo}" aggiunto!`);
    setShowAddModal(false);
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: '' });
  };

  const eliminaGioco = async (id, t) => {
    if (window.confirm(`Eliminare "${t}"?`)) {
      const lista = games.filter(g => g.id !== id);
      setGames(lista);
      if (window.location.hostname === 'localhost') await axios.post('http://localhost:5000/api/games/update', lista);
    }
  };

  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    if (!backlog.length) return;
    setIsSpinning(true); setChosenGame(null);
    let count = 0;
    const spin = setInterval(() => {
      setSpinTitle(backlog[Math.floor(Math.random() * backlog.length)].titolo);
      if (++count > 25) {
        clearInterval(spin);
        const finale = backlog[Math.floor(Math.random() * backlog.length)];
        setChosenGame(finale);
        setHistory(prev => [finale.titolo, ...prev].slice(0, 5));
        setIsSpinning(false);
      }
    }, 80);
  };

  // Stats & Helpers
  const sagaStats = games.reduce((acc, g) => {
    const nome = pulisciNomeSaga(g.saga) || 'Senza Saga';
    acc[nome] = (acc[nome] || 0) + 1;
    return acc;
  }, {});

  const anniPerFiltro = [...new Set(games.flatMap(g => dividiStringa(g.annoGiocato)))].sort((a,b) => b-a);
  const suggSaghe = [...new Set(games.map(g => g.saga))].filter(Boolean).sort();
  const suggPiatta = [...new Set(games.flatMap(g => dividiStringa(g.piattaforma)))].sort();

  const filteredGames = games.filter(g => 
    g.titolo?.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (filterYear === 'Tutti' || dividiStringa(g.annoGiocato).includes(filterYear)) &&
    (selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(g.saga) : pulisciNomeSaga(g.saga) === selectedSaga)) &&
    (filterStatus === 'Tutti' || g.stato === filterStatus)
  ).sort((a,b) => (selectedSaga !== 'Tutte' && selectedSaga !== 'Senza Saga') ? (parseInt(a.annoUscita)||9999) - (parseInt(b.annoUscita)||9999) : a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100vw', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', overflow: 'hidden' }}>
      
      <style>{`
        * { box-sizing: border-box; }
        html, body { margin: 0; padding: 0; background: #0a0a0a; overflow: hidden; }
        ::-webkit-scrollbar { width: 8px; }
        ::-webkit-scrollbar-track { background: #0a0a0a; }
        ::-webkit-scrollbar-thumb { background: #222; border-radius: 10px; }
        ::-webkit-scrollbar-thumb:hover { background: #00d1b2; }

        .sidebar { background: #111; border-right: 1px solid #222; overflow-y: auto; transition: 0.3s; z-index: 100; }
        .main-content { flex: 1; overflow-y: auto; padding: 40px; background: linear-gradient(135deg, #0a0a0a 0%, #111 100%); }
        .game-card { background: #161616; border: 1px solid #333; border-radius: 12px; overflow: hidden; transition: 0.2s; position: relative; }
        .game-card:hover { transform: translateY(-5px); border-color: #00d1b2; }
        
        .image-container { height: 320px; position: relative; overflow: hidden; background: #000; }
        .blur-bg { position: absolute; inset: -10px; background-size: cover; background-position: center; filter: blur(15px) brightness(0.3); z-index: 1; }
        .main-img { position: relative; width: 100%; height: 100%; object-fit: contain; z-index: 2; }
        
        .info-mask { position: absolute; inset: 0; background: rgba(0,0,0,0.85); display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; backdrop-filter: blur(5px); z-index: 5; padding: 20px; text-align: center; }
        .game-card:hover .info-mask { opacity: 1; }

        .platform-chip { background: rgba(0,209,178,0.1); color: #00d1b2; padding: 4px 8px; border-radius: 4px; font-size: 10px; font-weight: bold; margin: 2px; }
        .toast { position: fixed; bottom: 30px; left: 50%; transform: translateX(-50%); background: #27ae60; padding: 12px 30px; border-radius: 50px; z-index: 2000; animation: up 0.3s; }
        @keyframes up { from { bottom: -50px; } to { bottom: 30px; } }

        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.9); z-index: 1000; display: flex; align-items: center; justify-content: center; padding: 20px; backdrop-filter: blur(8px); }
        .modal-card { background: #161616; border-radius: 20px; width: 100%; max-width: 950px; display: flex; overflow: hidden; border: 1px solid #333; box-shadow: 0 0 50px rgba(0,0,0,0.5); }
      `}</style>

      <datalist id="s-saghe">{suggSaghe.map((s,i) => <option key={i} value={s}/>)}</datalist>
      <datalist id="s-piatta">{suggPiatta.map((p,i) => <option key={i} value={p}/>)}</datalist>

      {notification.show && <div className="toast"> {notification.message}</div>}

      {/* Sidebar */}
      <div className="sidebar" style={{ width: isCollapsed ? '0px' : '280px', padding: isCollapsed ? '0' : '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '25px' }}>
          <h2 style={{ color: '#00d1b2', margin: 0, fontSize: '18px' }}>LA MIA LISTA</h2>
          <button onClick={() => setIsCollapsed(true)} style={{ background: 'none', border: 'none', color: '#444', cursor: 'pointer' }}></button>
        </div>

        <button onClick={avviaSpinner} style={{ width: '100%', padding: '14px', background: 'linear-gradient(45deg, #f1c40f, #e67e22)', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '15px' }}> COSA GIOCO?</button>
        
        {isAdmin && (
          <div style={{ marginBottom: '20px', padding: '12px', background: '#1a1a1a', borderRadius: '10px', border: '1px solid #00d1b233' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#f1c40f', marginBottom: '10px' }}>
              <span> ADMIN MODES</span>
              <button onClick={() => setIsAdmin(false)} style={{ background: 'none', border: 'none', color: '#f1c40f', textDecoration: 'underline', cursor: 'pointer' }}>ESCI</button>
            </div>
            <button onClick={() => setShowAddModal(true)} style={{ width: '100%', padding: '10px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer' }}>+ AGGIUNGI</button>
          </div>
        )}

        <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#666' }} onClick={() => setSelectedSaga('Tutte')}>Tutte ({games.length})</div>
        <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Senza Saga' ? '#00d1b2' : '#666' }} onClick={() => setSelectedSaga('Senza Saga')}>Senza Saga ({sagaStats['Senza Saga']})</div>
        {Object.keys(sagaStats).filter(s => s !== 'Senza Saga').sort().map(s => (
          <div key={s} style={{ cursor: 'pointer', padding: '6px 10px', fontSize: '14px', color: selectedSaga === s ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga(s)}>
            {s} <span style={{ opacity: 0.4, fontSize: '11px' }}>({sagaStats[s]})</span>
          </div>
        ))}
      </div>

      {isCollapsed && <button onClick={() => setIsCollapsed(false)} style={{ position: 'fixed', top: '20px', left: '20px', background: '#111', border: '1px solid #333', color: '#00d1b2', padding: '10px', borderRadius: '8px', zIndex: 10 }}></button>}

      {/* Main Content */}
      <div className="main-content">
        <div style={{ display: 'flex', gap: '15px', marginBottom: '40px', flexWrap: 'wrap' }}>
          <input type="text" placeholder=" Cerca titolo..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 2, minWidth: '200px', padding: '15px', background: '#161616', border: '1px solid #333', borderRadius: '12px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333' }}>
            <option value="Tutti"> Anno Giocato</option>
            {anniPerFiltro.map(y => <option key={y} value={y}>{y}</option>)}
          </select>
          <select value={filterStatus} onChange={e => setFilterStatus(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333' }}>
            <option value="Tutti"> Stato</option>
            {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div className="image-container">
                <div className="blur-bg" style={{ backgroundImage: `url(${game.copertina})` }}></div>
                <img src={game.copertina} className="main-img" alt="" />
                
                <div className="info-mask">
                   {isAdmin ? (
                     <div style={{ width: '100%', display: 'flex', flexDirection: 'column', gap: '8px' }}>
                        <button onClick={() => eliminaGioco(game.id, game.titolo)} style={{ background: '#e74c3c', border: 'none', color: 'white', padding: '6px', borderRadius: '4px', cursor: 'pointer', fontWeight: 'bold' }}>ELIMINA</button>
                        <select style={{ padding: '6px', background: getColorStato(game.stato), color: 'white', border: 'none', borderRadius: '4px' }} value={game.stato} onChange={e => modificaCampo(game.id, 'stato', e.target.value)}>
                          {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                        </select>
                        <input list="s-saghe" style={{ padding: '6px', background: '#000', color: 'white', border: '1px solid #444', borderRadius: '4px' }} defaultValue={game.saga} onBlur={e => modificaCampo(game.id, 'saga', e.target.value)} placeholder="Saga" />
                        <input list="s-piatta" style={{ padding: '6px', background: '#000', color: '#00d1b2', border: '1px solid #444', borderRadius: '4px' }} defaultValue={game.piattaforma} onBlur={e => modificaCampo(game.id, 'piattaforma', e.target.value)} placeholder="Piattaforme" />
                     </div>
                   ) : (
                     <>
                        <h3 style={{ color: '#00d1b2', margin: '0 0 10px 0' }}>{game.saga || ' '}</h3>
                        <p style={{ fontSize: '13px', color: '#ccc' }}>Giocato: {game.annoGiocato || '---'}</p>
                        <p style={{ fontSize: '11px', color: '#f1c40f', fontStyle: 'italic' }}>{game.note}</p>
                     </>
                   )}
                </div>
              </div>

              <div style={{ padding: '20px' }}>
                <div style={{ fontSize: '10px', color: getColorStato(game.stato), fontWeight: 'bold', marginBottom: '8px' }}>{game.stato.toUpperCase()}</div>
                <h4 style={{ margin: 0, fontSize: '18px', lineHeight: '1.2' }}>
                  {game.titolo} {game.annoUscita && <span style={{ color: '#444', fontSize: '14px' }}>({game.annoUscita})</span>}
                </h4>
                <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '12px' }}>
                  {dividiStringa(game.piattaforma).map((p, i) => <span key={i} className="platform-chip">{p}</span>)}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal Aggiungi */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal-card">
            <div style={{ flex: 1, padding: '40px', borderRight: '1px solid #333' }}>
              <h2 style={{ color: '#00d1b2', marginTop: 0 }}>Nuovo Gioco</h2>
              <form onSubmit={aggiungiGioco}>
                <input placeholder="Titolo" required style={{ width: '100%', padding: '12px', marginBottom: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
                <input placeholder="URL Copertina" required style={{ width: '100%', padding: '12px', marginBottom: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
                <div style={{ display: 'flex', gap: '10px', marginBottom: '12px' }}>
                  <input placeholder="Saga" list="s-saghe" style={{ flex: 1, padding: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
                  <input placeholder="Uscita (Anno)" style={{ width: '100px', padding: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.annoUscita} onChange={e => setNewGame({...newGame, annoUscita: e.target.value})} />
                </div>
                <input placeholder="Piattaforme (PC, PS5...)" list="s-piatta" style={{ width: '100%', padding: '12px', marginBottom: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.piattaforma} onChange={e => setNewGame({...newGame, piattaforma: e.target.value})} />
                <textarea placeholder="Note personali..." style={{ width: '100%', padding: '12px', height: '80px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px', marginBottom: '20px' }} value={newGame.note} onChange={e => setNewGame({...newGame, note: e.target.value})} />
                <div style={{ display: 'flex', gap: '10px' }}>
                  <button type="button" onClick={() => setShowAddModal(false)} style={{ flex: 1, padding: '15px', background: '#333', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer' }}>ANNULLA</button>
                  <button type="submit" style={{ flex: 2, padding: '15px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer' }}>SALVA GIOCO</button>
                </div>
              </form>
            </div>
            <div style={{ flex: 1, background: '#0d0d0d', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: '40px' }}>
              <p style={{ color: '#444', fontSize: '10px', textTransform: 'uppercase', marginBottom: '20px' }}>Preview Card</p>
              <div className="game-card" style={{ width: '260px' }}>
                <div className="image-container">
                  <div className="blur-bg" style={{ backgroundImage: `url(${newGame.copertina})` }}></div>
                  <img src={newGame.copertina || 'https://via.placeholder.com/300x400?text=No+Cover'} className="main-img" alt="" />
                </div>
                <div style={{ padding: '15px' }}>
                  <div style={{ fontSize: '10px', color: '#7f8c8d', fontWeight: 'bold', marginBottom: '5px' }}>NON GIOCATO</div>
                  <h4 style={{ margin: 0 }}>{newGame.titolo || 'Titolo...'}</h4>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Spinner Overlay */}
      {(isSpinning || chosenGame) && (
        <div className="modal-overlay">
          <div style={{ background: '#161616', padding: '40px', borderRadius: '30px', textAlign: 'center', border: '2px solid #f1c40f', maxWidth: '500px', width: '90%' }}>
            {isSpinning ? (
              <>
                <h2 style={{ color: '#f1c40f' }}>ESTRAZIONE IN CORSO...</h2>
                <h1 style={{ height: '80px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>{spinTitle}</h1>
              </>
            ) : (
              <>
                <h2 style={{ color: '#27ae60' }}>PROSSIMO GIOCO:</h2>
                <img src={chosenGame.copertina} style={{ width: '100%', maxHeight: '400px', objectFit: 'contain', borderRadius: '15px', margin: '20px 0' }} alt="" />
                <h1>{chosenGame.titolo}</h1>
                <button onClick={() => setChosenGame(null)} style={{ background: '#00d1b2', color: 'black', border: 'none', padding: '15px 40px', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer' }}>ANDIAMO!</button>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
