import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterStatus, setFilterStatus] = useState('Tutti');
  const [showAddModal, setShowAddModal] = useState(false); // Modal state
  const [isAdmin, setIsAdmin] = useState(false);
  const [notification, setNotification] = useState({ show: false, message: '' });
  
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);

  // Password e Accesso Admin (Shift + A)
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey && (e.key === 'A' || e.key === 'a')) {
        const passwordSegreta = "listone";
        const inserimento = prompt("Accesso protetto. Inserisci codice:");
        if (inserimento === passwordSegreta) setIsAdmin(true);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('spinHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', piattaforma: '', stato: 'Non Giocato', note: ''
  });

  useEffect(() => { localStorage.setItem('spinHistory', JSON.stringify(history)); }, [history]);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const url = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/games' : '/games.json';
        const res = await axios.get(url);
        setGames(res.data);
      } catch (err) { console.error("Errore caricamento dati:", err); }
    };
    fetchGames();
  }, []);

  const showToast = (msg) => {
    setNotification({ show: true, message: msg });
    setTimeout(() => setNotification({ show: false, message: '' }), 3000);
  };

  const dividiStringa = (valore) => valore ? String(valore).split(',').map(item => item.trim()).filter(i => i !== "") : [];
  const getColorStato = (stato) => ({ 'Completato': '#27ae60', 'In corso': '#f1c40f', 'Droppato': '#e74c3c', 'Sospeso': '#e67e22' }[stato] || '#7f8c8d');
  const pulisciNomeSaga = (saga) => saga ? String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim() : "";

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const nuovoGiocoConId = { ...newGame, id: Date.now() };
    const nuovaLista = [...games, nuovoGiocoConId];
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
    showToast(`"${newGame.titolo}" aggiunto con successo!`);
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', piattaforma: '', stato: 'Non Giocato', note: '' });
    setShowAddModal(false);
  };

  const sagaStats = games.reduce((acc, g) => {
    const nome = pulisciNomeSaga(g.saga) || 'Senza Saga';
    acc[nome] = (acc[nome] || 0) + 1;
    return acc;
  }, {});

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      const matchesStatus = filterStatus === 'Tutti' || game.stato === filterStatus;
      return matchesSearch && matchesSaga && matchesStatus;
    })
    .sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100vw', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', overflow: 'hidden' }}>
      
      <style>{`
        /* RESET GLOBALE PER ELIMINARE BORDI BIANCHI */
        * { box-sizing: border-box; }
        html, body { margin: 0; padding: 0; background-color: #0a0a0a; overflow: hidden; width: 100%; height: 100%; }
        
        /* SCROLLBAR PERSONALIZZATA DARK */
        ::-webkit-scrollbar { width: 8px; height: 8px; }
        ::-webkit-scrollbar-track { background: #0a0a0a; }
        ::-webkit-scrollbar-thumb { background: #222; border-radius: 10px; border: 2px solid #0a0a0a; }
        ::-webkit-scrollbar-thumb:hover { background: #00d1b2; }

        /* ANIMAZIONI E CLASSI */
        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.85); z-index: 1000; display: flex; align-items: center; justify-content: center; backdrop-filter: blur(5px); padding: 20px; }
        .modal-card { background: #161616; border-radius: 20px; border: 1px solid #333; width: 100%; max-width: 900px; display: flex; overflow: hidden; box-shadow: 0 25px 50px rgba(0,0,0,0.5); }
        .form-side { flex: 1; padding: 40px; border-right: 1px solid #333; }
        .preview-side { flex: 1; padding: 40px; background: #0d0d0d; display: flex; flex-direction: column; align-items: center; justify-content: center; }
        .toast { position: fixed; bottom: 30px; left: 50%; transform: translateX(-50%); background: #27ae60; color: white; padding: 12px 30px; border-radius: 50px; font-weight: bold; z-index: 2000; animation: slideUp 0.4s ease; box-shadow: 0 10px 20px rgba(0,0,0,0.3); }
        @keyframes slideUp { from { bottom: -60px; opacity: 0; } to { bottom: 30px; opacity: 1; } }
        
        .game-card { background: #161616; border: 1px solid #333; border-radius: 12px; overflow: hidden; transition: 0.3s; }
        .platform-chip { background: rgba(0, 209, 178, 0.1); color: #00d1b2; padding: 4px 8px; border-radius: 4px; font-size: 11px; margin: 2px; display: inline-block; }
      `}</style>

      {/* NOTIFICA */}
      {notification.show && <div className="toast">✅ {notification.message}</div>}

      {/* MODAL AGGIUNGI GIOCO */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal-card">
            <div className="form-side">
              <h2 style={{ color: '#00d1b2', marginTop: 0 }}>Nuovo Gioco</h2>
              <form onSubmit={aggiungiGioco}>
                <input placeholder="Titolo" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
                <input placeholder="URL Copertina" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
                <input placeholder="Saga (es: Fallout Series)" style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
                <input placeholder="Piattaforme (es: PC, PS5)" style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.piattaforma} onChange={e => setNewGame({...newGame, piattaforma: e.target.value})} />
                <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
                  <button type="button" onClick={() => setShowAddModal(false)} style={{ flex: 1, padding: '15px', background: '#333', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer' }}>ANNULLA</button>
                  <button type="submit" style={{ flex: 2, padding: '15px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer' }}>SALVA GIOCO</button>
                </div>
              </form>
            </div>
            <div className="preview-side">
              <p style={{ color: '#555', fontSize: '12px', textTransform: 'uppercase', marginBottom: '20px' }}>Anteprima Card</p>
              <div className="game-card" style={{ width: '260px' }}>
                <div style={{ height: '320px', background: '#000' }}>
                  <img src={newGame.copertina || 'https://via.placeholder.com/300x400?text=Anteprima'} style={{ width: '100%', height: '100%', objectFit: 'contain' }} alt="" />
                </div>
                <div style={{ padding: '15px' }}>
                  <div style={{ fontSize: '10px', color: '#777', fontWeight: 'bold', marginBottom: '5px' }}>NON GIOCATO</div>
                  <h4 style={{ margin: 0 }}>{newGame.titolo || 'Titolo Gioco'}</h4>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Sidebar */}
      <div className="sidebar" style={{ width: isCollapsed ? '70px' : '280px', background: '#111', borderRight: '1px solid #222', padding: '20px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          {!isCollapsed && <h2 style={{ color: '#00d1b2', margin: 0, fontSize: '18px' }}>COLLEZIONE</h2>}
          <button onClick={() => setIsCollapsed(!isCollapsed)} style={{ background: 'none', border: 'none', color: '#444', cursor: 'pointer' }}>{isCollapsed ? '➡' : '⬅'}</button>
        </div>

        {!isCollapsed && (
          <>
            {isAdmin && (
              <button onClick={() => setShowAddModal(true)} style={{ width: '100%', padding: '12px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>+ AGGIUNGI GIOCO</button>
            )}
            <p style={{ color: '#444', fontSize: '11px', fontWeight: 'bold', textTransform: 'uppercase' }}>Saghe</p>
            <div style={{ cursor: 'pointer', padding: '8px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte ({games.length})</div>
            {Object.keys(sagaStats).sort().map(s => (
              <div key={s} style={{ cursor: 'pointer', padding: '8px', color: selectedSaga === s ? '#00d1b2' : '#888', fontSize: '14px' }} onClick={() => setSelectedSaga(s)}>
                {s} <span style={{ opacity: 0.5, fontSize: '11px' }}>({sagaStats[s]})</span>
              </div>
            ))}
          </>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content" style={{ flex: 1, padding: '40px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '40px' }}>
          <input type="text" placeholder="Cerca gioco..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', border: '1px solid #333', borderRadius: '12px', color: 'white' }} />
          <select value={filterStatus} onChange={e => setFilterStatus(e.target.value)} style={{ padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333' }}>
            <option value="Tutti">Tutti gli stati</option>
            {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div style={{ height: '320px', background: '#000', position: 'relative' }}>
                <img src={game.copertina} style={{ width: '100%', height: '100%', objectFit: 'contain' }} alt={game.titolo} />
              </div>
              <div style={{ padding: '20px' }}>
                <div style={{ fontSize: '10px', fontWeight: 'bold', color: getColorStato(game.stato), marginBottom: '8px' }}>{game.stato.toUpperCase()}</div>
                <h4 style={{ margin: '0 0 10px 0', fontSize: '18px' }}>{game.titolo}</h4>
                <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                  {dividiStringa(game.piattaforma).map((p, i) => <span key={i} className="platform-chip">{p}</span>)}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
