import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [loading, setLoading] = useState(true);

  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Database sincronizzato!");
    } catch (err) {
      alert("Errore nell'upload");
    } finally {
      setLoading(false);
    }
  };

  const updateGameData = async (updatedList) => {
    try {
      await axios.post('http://localhost:5000/api/games/update', updatedList);
    } catch (err) {
      console.error("Errore salvataggio");
    }
  };

  const salvaModificaSaga = (id, nuovaSaga) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, saga: nuovaSaga.trim() } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const cambiaStato = (id, nuovoStato) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, stato: nuovoStato } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return 'rgba(39, 174, 96, 0.9)'; // Verde
      case 'In corso': return 'rgba(241, 196, 15, 0.9)';   // Giallo
      case 'Droppato': return 'rgba(231, 76, 60, 0.9)';   // Rosso
      case 'Sospeso': return 'rgba(230, 126, 34, 0.9)';    // Arancione
      default: return 'rgba(40, 40, 40, 0.9)';            // Grigio
    }
  };

  const saghePopolate = [...new Set(games.map(g => g.saga))].filter(s => s && s.trim() !== "").sort();
  const saghePerSidebar = ['Tutte', 'Senza Saga', ...saghePopolate];

  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    let matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !game.saga : game.saga === selectedSaga);
    return matchesSearch && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#121212', color: 'white', fontFamily: 'Segoe UI' }}>
      
      <style>{`
        .game-card { position: relative; overflow: hidden; border-radius: 12px; background: #1e1e1e; border: 1px solid #333; }
        
        /* La Maschera come da tuo disegno */
        .info-mask {
          position: absolute;
          top: 0; left: 0; right: 0; height: 280px; /* Copre esattamente l'immagine */
          display: flex; flex-direction: column; align-items: center; justify-content: center;
          opacity: 0; transition: opacity 0.3s ease;
          z-index: 5; text-align: center; padding: 20px;
        }

        .game-card:hover .info-mask { opacity: 1; }

        .mask-label { font-size: 10px; text-transform: uppercase; letter-spacing: 1px; margin-bottom: 5px; color: rgba(255,255,255,0.7); }
        .mask-value { font-size: 18px; font-weight: bold; margin-bottom: 20px; }
        
        .status-select-large {
          background: rgba(255,255,255,0.2); border: 1px solid white; color: white;
          padding: 8px; border-radius: 6px; cursor: pointer; font-size: 14px; width: 80%;
        }
        .status-select-large option { background: #333; }
      `}</style>

      <datalist id="saghe-list">
        {saghePopolate.map((s, i) => <option key={i} value={s} />)}
      </datalist>

      {/* Sidebar */}
      <div style={{ width: '250px', padding: '20px', borderRight: '1px solid #333', backgroundColor: '#181818' }}>
        <h3 style={{ color: '#00d1b2' }}>Esplora</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {saghePerSidebar.map(s => (
            <li key={s} onClick={() => setSelectedSaga(s)} style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === s ? '#00d1b2' : '#bbb' }}>{s}</li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px' }}>
        <div style={{ marginBottom: '30px', display: 'flex', gap: '20px' }}>
          <input type="text" placeholder="Cerca un gioco..." onChange={(e) => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '15px', background: '#222', color: 'white', border: '1px solid #444', borderRadius: '8px' }} />
          <input type="file" accept=".xlsx" onChange={handleFileUpload} />
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))', gap: '25px' }}>
          {filteredGames.map((game) => (
            <div key={game.id} className="game-card">
              
              {/* MASCHERA CHE APPARE ALL'HOVER */}
              <div className="info-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                <div className="mask-label">Data Gioco</div>
                <div className="mask-value">{game.annoGiocato && game.annoGiocato !== "-" ? game.annoGiocato : "Nessuna"}</div>
                
                <div className="mask-label">Stato</div>
                <select 
                  className="status-select-large"
                  value={game.stato || 'Non Giocato'}
                  onChange={(e) => cambiaStato(game.id, e.target.value)}
                >
                  <option value="Non Giocato">Non Giocato</option>
                  <option value="Completato">Completato</option>
                  <option value="In corso">In corso</option>
                  <option value="Sospeso">Sospeso</option>
                  <option value="Droppato">Droppato</option>
                </select>
              </div>

              {/* Immagine di Sfondo */}
              <img 
                src={game.copertina} alt={game.titolo} 
                style={{ width: '100%', height: '280px', objectFit: 'cover' }} 
                onError={(e) => { e.target.src = 'https://via.placeholder.com/200x280?text=No+Cover'; }} 
              />
              
              {/* Info fisse sotto (Titolo e Saga) */}
              <div style={{ padding: '15px' }}>
                <h4 style={{ margin: '0 0 5px 0' }}>{game.titolo}</h4>
                <div style={{ fontSize: '11px', color: '#00d1b2' }}>{game.piattaforma}</div>
                <div style={{ marginTop: '10px', borderTop: '1px solid #333', paddingTop: '10px' }}>
                  <input 
                    type="text" defaultValue={game.saga} placeholder="Nessuna saga..." list="saghe-list"
                    onBlur={(e) => salvaModificaSaga(game.id, e.target.value)}
                    style={{ background: 'transparent', border: 'none', color: '#666', fontSize: '12px', width: '100%', outline: 'none' }}
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
