import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [filterStatus, setFilterStatus] = useState('Tutti');
  const [showAddModal, setShowAddModal] = useState(false); // Modal Add
  const [isAdmin, setIsAdmin] = useState(false);
  const [notification, setNotification] = useState({ show: false, message: '' });
  
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);

  // Password e Accesso Admin
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey && (e.key === 'A' || e.key === 'a')) {
        const passwordSegreta = "listone";
        const inserimento = prompt("Accesso protetto. Inserisci codice:");
        if (inserimento === passwordSegreta) setIsAdmin(true);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('spinHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: ''
  });

  useEffect(() => { localStorage.setItem('spinHistory', JSON.stringify(history)); }, [history]);

  const fetchGames = async () => {
    try {
      const url = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/games' : '/games.json';
      const res = await axios.get(url);
      setGames(res.data);
    } catch (err) { console.error("Errore caricamento dati:", err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const showToast = (msg) => {
    setNotification({ show: true, message: msg });
    setTimeout(() => setNotification({ show: false, message: '' }), 3000);
  };

  const dividiStringa = (valore) => {
    if (!valore) return [];
    return String(valore).split(',').map(item => item.trim()).filter(item => item !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60';
      case 'In corso': return '#f1c40f';
      case 'Droppato': return '#e74c3c';
      case 'Sospeso': return '#e67e22';
      default: return '#7f8c8d';
    }
  };

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const modificaCampo = async (id, campo, valore) => {
    if (!isAdmin) return;
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
  };

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const nuovoGiocoConId = { ...newGame, id: Date.now() };
    const nuovaLista = [...games, nuovoGiocoConId];
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
    showToast(`"${newGame.titolo}" aggiunto correttamente!`);
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: '' });
    setShowAddModal(false);
  };

  const eliminaGioco = async (id, titolo) => {
    if (!isAdmin) return;
    if (window.confirm(`Eliminare "${titolo}"?`)) {
      const nuovaLista = games.filter(g => g.id !== id);
      setGames(nuovaLista);
      if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
      }
    }
  };

  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    if (backlog.length === 0) return alert("Nessun gioco 'Non Giocato'!");
    setIsSpinning(true);
    setChosenGame(null);
    let durataTotale = 3000;
    let intervallo = 50;
    let tempoTrascorso = 0;
    const spin = () => {
      const randomTmp = backlog[Math.floor(Math.random() * backlog.length)];
      setSpinTitle(randomTmp.titolo);
      tempoTrascorso += intervallo;
      if (tempoTrascorso < durataTotale) setTimeout(spin, intervallo);
      else {
        const finale = backlog[Math.floor(Math.random() * backlog.length)];
        setChosenGame(finale);
        setHistory(prev => [finale.titolo, ...prev].slice(0, 5));
        setIsSpinning(false);
      }
    };
    spin();
  };

  const getSagaStats = () => {
    const stats = { 'Senza Saga': 0 };
    games.forEach(g => {
      const nomePulito = pulisciNomeSaga(g.saga);
      if (!nomePulito) stats['Senza Saga']++;
      else stats[nomePulito] = (stats[nomePulito] || 0) + 1;
    });
    return stats;
  };
  const sagaStats = getSagaStats();

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      const matchesStatus = filterStatus === 'Tutti' || game.stato === filterStatus;
      return matchesSearch && matchesSaga && matchesStatus;
    })
    .sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100%', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', overflow: 'hidden', position: 'relative' }}>
      
      {/* CSS AGGIUNTIVO PER MODAL E NOTIFICHE */}
      <style>{`
        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.85); z-index: 1000; display: flex; align-items: center; justify-content: center; backdrop-filter: blur(5px); padding: 20px; }
        .modal-card { background: #1a1a1a; border-radius: 20px; border: 1px solid #333; width: 100%; max-width: 900px; max-height: 90vh; overflow-y: auto; display: flex; box-shadow: 0 20px 50px rgba(0,0,0,0.5); }
        .modal-form-side { flex: 1; padding: 30px; border-right: 1px solid #333; }
        .modal-preview-side { flex: 1; padding: 30px; background: #111; display: flex; flex-direction: column; align-items: center; justify-content: center; }
        .toast { position: fixed; bottom: 30px; left: 50%; transform: translateX(-50%); background: #27ae60; color: white; padding: 12px 30px; borderRadius: 50px; font-weight: bold; z-index: 2000; animation: slideUp 0.3s ease; box-shadow: 0 10px 20px rgba(0,0,0,0.3); }
        @keyframes slideUp { from { bottom: -50px; opacity: 0; } to { bottom: 30px; opacity: 1; } }
        @media (max-width: 768px) { .modal-card { flex-direction: column; } .modal-form-side { border-right: none; border-bottom: 1px solid #333; } }
        
        /* CARD STYLES REUSED */
        .game-card { border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; width: 260px; }
        .platform-chip { background: rgba(0, 209, 178, 0.15); color: #00d1b2; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; }
        .status-badge { font-size: 10px; text-transform: uppercase; font-weight: 900; padding: 2px 8px; border-radius: 4px; display: inline-block; margin-bottom: 8px; }
        .image-container { position: relative; height: 320px; background: #000; overflow: hidden; }
        .main-img { width: 100%; height: 100%; object-fit: contain; }
        .sidebar { transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); background: #111; overflow-y: auto; flex-shrink: 0; border-right: 1px solid #333; }
      `}</style>

      {/* NOTIFICA TOAST */}
      {notification.show && <div className="toast">✅ {notification.message}</div>}

      {/* MODAL AGGIUNGI GIOCO */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal-card">
            {/* Sinistra: Form */}
            <div className="modal-form-side">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                <h2 style={{ margin: 0, color: '#00d1b2' }}>Nuovo Titolo</h2>
                <button onClick={() => setShowAddModal(false)} style={{ background: 'none', border: 'none', color: '#666', fontSize: '24px', cursor: 'pointer' }}>✕</button>
              </div>
              <form onSubmit={aggiungiGioco}>
                <input placeholder="Titolo del gioco" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
                <input placeholder="URL Immagine Copertina" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
                <input placeholder="Saga (es: Fallout Series)" list="lista-saghe" style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
                <input placeholder="Piattaforme (es: PC, PS5)" list="lista-piattaforme" style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.piattaforma} onChange={e => setNewGame({...newGame, piattaforma: e.target.value})} />
                <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
                  <input placeholder="Anno Uscita" style={{ flex: 1, padding: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.annoUscita} onChange={e => setNewGame({...newGame, annoUscita: e.target.value})} />
                  <select style={{ flex: 1, padding: '12px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.stato} onChange={e => setNewGame({...newGame, stato: e.target.value})}>
                    {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                  </select>
                </div>
                <button type="submit" style={{ width: '100%', padding: '15px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', fontSize: '16px' }}>SALVA NELLA COLLEZIONE</button>
              </form>
            </div>

            {/* Destra: Anteprima Card */}
            <div className="modal-preview-side">
              <p style={{ color: '#666', fontSize: '12px', textTransform: 'uppercase', letterSpacing: '1px', marginBottom: '15px' }}>Anteprima Card</p>
              <div className="game-card">
                <div className="image-container">
                  <img src={newGame.copertina || 'https://via.placeholder.com/300x400?text=No+Image'} className="main-img" alt="preview" />
                </div>
                <div style={{ padding: '15px' }}>
                  <div className="status-badge" style={{ backgroundColor: getColorStato(newGame.stato) }}>{newGame.stato}</div>
                  <h4 style={{ margin: '0 0 10px 0', fontSize: '16px' }}>
                    {newGame.titolo || 'Titolo Gioco'} 
                    {newGame.annoUscita && <span style={{ color: '#777', fontSize: '12px', marginLeft: '5px' }}>({newGame.annoUscita})</span>}
                  </h4>
                  <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {dividiStringa(newGame.piattaforma).map((p, i) => <span key={i} className="platform-chip">{p}</span>)}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Sidebar */}
      <div className="sidebar" style={{ width: isCollapsed ? '70px' : '280px', padding: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: isCollapsed ? 'center' : 'space-between', marginBottom: '25px' }}>
          {!isCollapsed && <h2 style={{ color: '#00d1b2', margin: 0, fontSize: '20px' }}>🎮 LISTA</h2>}
          <button onClick={() => setIsCollapsed(!isCollapsed)} style={{ background: '#222', border: 'none', color: '#00d1b2', cursor: 'pointer', borderRadius: '6px', padding: '5px' }}>{isCollapsed ? '➡' : '⬅'}</button>
        </div>

        {!isCollapsed && (
          <>
            <div style={{ fontSize: '12px', color: '#666', marginBottom: '20px', background: '#1a1a1a', padding: '10px', borderRadius: '8px' }}>
              Totale: <b>{games.length}</b>
              {isAdmin && (
                <div style={{ marginTop: '10px', paddingTop: '10px', borderTop: '1px solid #333', display: 'flex', justifyContent: 'space-between' }}>
                  <span style={{ color: '#f1c40f', fontWeight: 'bold' }}>ADMIN</span>
                  <button onClick={() => setIsAdmin(false)} style={{ background: 'none', border: 'none', color: '#f1c40f', cursor: 'pointer', textDecoration: 'underline', padding: 0 }}>ESCI</button>
                </div>
              )}
            </div>
            
            <button className="btn-spin" style={{ width: '100%', padding: '12px', background: 'linear-gradient(45deg, #f1c40f, #e67e22)', border: 'none', borderRadius: '10px', fontWeight: 'bold', marginBottom: '10px', cursor: 'pointer' }} onClick={avviaSpinner}>🎲 COSA GIOCO?</button>

            {isAdmin && (
              <button onClick={() => setShowAddModal(true)} style={{ width: '100%', padding: '12px', background: '#00d1b2', color: '#000', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>+ AGGIUNGI GIOCO</button>
            )}

            <p style={{ color: '#444', fontSize: '11px', fontWeight: 'bold', textTransform: 'uppercase' }}>Saghe</p>
            <div style={{ cursor: 'pointer', padding: '8px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte ({games.length})</div>
            {Object.keys(sagaStats).filter(s => s !== 'Senza Saga').sort().map(s => (
              <div key={s} style={{ cursor: 'pointer', padding: '8px', color: selectedSaga === s ? '#00d1b2' : '#888', fontSize: '14px' }} onClick={() => setSelectedSaga(s)}>
                {s} <span style={{ opacity: 0.5, fontSize: '11px' }}>({sagaStats[s]})</span>
              </div>
            ))}
          </>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content" style={{ flex: 1, padding: '40px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '40px' }}>
          <input type="text" placeholder="🔍 Cerca..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', border: '1px solid #333', borderRadius: '12px', color: 'white' }} />
          <select value={filterStatus} onChange={e => setFilterStatus(e.target.value)} style={{ padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333' }}>
            <option value="Tutti">Tutti gli Stati</option>
            {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div className="image-container">
                <img src={game.copertina} className="main-img" alt={game.titolo} />
                <div style={{ position: 'absolute', inset: 0, background: 'rgba(0,0,0,0.7)', opacity: 0, transition: '0.3s', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }} className="hover-info">
                   {isAdmin && <button onClick={() => eliminaGioco(game.id, game.titolo)} style={{ background: '#e74c3c', border: 'none', color: 'white', padding: '8px 15px', borderRadius: '6px', cursor: 'pointer', marginBottom: '10px' }}>Elimina</button>}
                   <p style={{ color: '#00d1b2', fontWeight: 'bold' }}>{game.saga || 'Senza Saga'}</p>
                   <p style={{ fontSize: '12px' }}>{game.note}</p>
                </div>
              </div>
              <div style={{ padding: '20px' }}>
                <div className="status-badge" style={{ backgroundColor: getColorStato(game.stato) }}>{game.stato}</div>
                <h4 style={{ margin: '0 0 10px 0', fontSize: '18px' }}>
                  {game.titolo} {game.annoUscita && <span style={{ color: '#777', fontWeight: 'normal', fontSize: '14px' }}>({game.annoUscita})</span>}
                </h4>
                <div>{dividiStringa(game.piattaforma).map((p, i) => <span key={i} className="platform-chip">{p}</span>)}</div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <style>{`.game-card:hover .hover-info { opacity: 1 !important; }`}</style>
    </div>
  );
};

export default App;
