import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [sortType, setSortType] = useState('titolo');
  const [loading, setLoading] = useState(true);

  // --- LOGICA DI FORMATTAZIONE E UTILITY ---

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return 'rgba(39, 174, 96, 0.85)';
      case 'In corso': return 'rgba(241, 196, 15, 0.85)';
      case 'Droppato': return 'rgba(231, 76, 60, 0.85)';
      case 'Sospeso': return 'rgba(230, 126, 34, 0.85)';
      default: return 'rgba(40, 40, 40, 0.85)';
    }
  };

  const formattaLista = (valore) => {
    if (!valore) return [];
    const str = String(valore);
    // Separa per virgola, slash o quando trova un cambio minuscola->Maiuscola (es. PS3PS4)
    return str.split(/[,/]|(?<=[0-9a-z])(?=[A-Z])/).map(s => s.trim()).filter(s => s !== "");
  };

  const getAnniGioco = (valore) => {
    const anni = formattaLista(valore);
    return {
      lista: anni,
      isRigiocato: anni.length > 1
    };
  };

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    const sagaStr = String(saga).trim();
    const eccezioni = ["Remedy Connected Universe"];
    if (eccezioni.includes(sagaStr)) return sagaStr;
    return sagaStr.replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  // --- API ---

  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchGames(); }, []);

  const updateGameData = async (updatedList) => {
    try { await axios.post('http://localhost:5000/api/games/update', updatedList); } 
    catch (err) { console.error("Errore salvataggio"); }
  };

  const modificaCampo = (id, campo, valore) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const cambiaStato = (id, nuovoStato) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, stato: nuovoStato } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  // --- FILTRI ---

  const resetFiltri = () => {
    setSelectedSaga('Tutte');
    setFilterYear('Tutti');
    setSearchTerm('');
  };

  const anniDisponibili = [...new Set(games.flatMap(g => formattaLista(g.annoGiocato)))]
    .filter(a => a && a !== "-" && a.length === 4)
    .sort((a, b) => b - a);

  const saghePopolate = [...new Set(games.map(g => pulisciNomeSaga(g.saga)))]
    .filter(s => s && s !== "").sort();

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const sagaVisualizzata = pulisciNomeSaga(game.saga);
      const anniGame = formattaLista(game.annoGiocato);
      
      let matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !sagaVisualizzata : sagaVisualizzata === selectedSaga);
      let matchesYear = filterYear === 'Tutti' || anniGame.includes(filterYear);
      
      return matchesSearch && matchesSaga && matchesYear;
    })
    .sort((a, b) => {
      const getPrimoAnno = (val) => parseInt(formattaLista(val)[0], 10) || 0;
      switch (sortType) {
        case 'titolo': return a.titolo.localeCompare(b.titolo);
        case 'uscita-desc': return getPrimoAnno(b.annoUscita) - getPrimoAnno(a.annoUscita);
        case 'uscita-asc': return getPrimoAnno(a.annoUscita) - getPrimoAnno(b.annoUscita);
        case 'giocato-desc': return getPrimoAnno(b.annoGiocato) - getPrimoAnno(a.annoGiocato);
        case 'giocato-asc': return getPrimoAnno(a.annoGiocato) - getPrimoAnno(b.annoGiocato);
        default: return 0;
      }
    });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI' }}>
      
      <style>{`
        .game-card { position: relative; border-radius: 12px; background: #181818; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; transition: 0.3s; }
        .game-card:hover { transform: translateY(-5px); border-color: #00d1b2; }
        .sidebar-item { cursor: pointer; padding: 10px 15px; border-radius: 8px; transition: 0.2s; color: #888; font-size: 14px; }
        .sidebar-item:hover { color: #00d1b2; background: rgba(0, 209, 178, 0.05); }
        .sidebar-item.active { background: rgba(0, 209, 178, 0.15); color: #00d1b2; font-weight: bold; }
        .platform-chip { background: rgba(0, 209, 178, 0.1); color: #00d1b2; padding: 2px 8px; border-radius: 4px; font-size: 10px; font-weight: bold; margin-right: 5px; display: inline-block; margin-bottom: 4px; }
        .replay-badge { position: absolute; top: 12px; right: 12px; background: #f1c40f; color: #000; padding: 5px; border-radius: 50%; font-size: 14px; z-index: 10; line-height: 1; box-shadow: 0 0 10px rgba(0,0,0,0.5); }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(8px); }
        .game-card:hover .info-mask { opacity: 1; }
      `}</style>

      {/* Sidebar */}
      <div style={{ width: '280px', padding: '25px', borderRight: '1px solid #333', backgroundColor: '#111', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto' }}>
        <h2 style={{ color: '#00d1b2', fontSize: '1.4rem', marginBottom: '25px' }}>ðŸŽ® GameLib</h2>
        <button onClick={resetFiltri} style={{ width: '100%', padding: '12px', background: '#00d1b2', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '10px' }}>MOSTRA TUTTO</button>
        <div style={{ fontSize: '12px', color: '#555', textAlign: 'center', marginBottom: '25px' }}>{filteredGames.length} titoli trovati</div>

        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li onClick={() => setSelectedSaga('Tutte')} className={`sidebar-item ${selectedSaga === 'Tutte' ? 'active' : ''}`}>Tutte le Saghe</li>
          <li onClick={() => setSelectedSaga('Senza Saga')} className={`sidebar-item ${selectedSaga === 'Senza Saga' ? 'active' : ''}`}>Senza Saga</li>
          {saghePopolate.map(s => (
            <li key={s} onClick={() => setSelectedSaga(s)} className={`sidebar-item ${selectedSaga === s ? 'active' : ''}`}>{s}</li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '40px' }}>
        <div style={{ marginBottom: '40px', display: 'flex', gap: '20px', alignItems: 'center' }}>
          <input type="text" value={searchTerm} placeholder="Cerca titolo..." onChange={(e) => setSearchTerm(e.target.value)} 
                 style={{ flex: 1, padding: '15px', background: '#1a1a1a', border: '1px solid #333', borderRadius: '12px', color: 'white', outline: 'none' }} />
          
          <select value={filterYear} onChange={(e) => setFilterYear(e.target.value)} style={{ padding: '15px', background: '#1a1a1a', color: '#00d1b2', border: '1px solid #333', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer' }}>
            <option value="Tutti">ðŸ“… Qualsiasi Anno</option>
            {anniDisponibili.map(anno => <option key={anno} value={anno}>{anno}</option>)}
          </select>

          <select value={sortType} onChange={(e) => setSortType(e.target.value)} style={{ padding: '15px', background: '#1a1a1a', color: '#00d1b2', border: '1px solid #333', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer' }}>
            <option value="titolo">Nome A-Z</option>
            <option value="uscita-desc">Uscita â†“</option>
            <option value="giocato-desc">Giocato â†“</option>
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map((game) => {
            const piattaforme = formattaLista(game.piattaforma);
            const infoGioco = getAnniGioco(game.annoGiocato);

            return (
              <div key={game.id} className="game-card">
                {infoGioco.isRigiocato && <div className="replay-badge" title="Rigiocato">ðŸ”„</div>}

                <div style={{ position: 'relative', height: '340px', background: '#000', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <img src={game.copertina} style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover', filter: 'blur(10px) brightness(0.3)' }} alt="" />
                  
                  <div className="info-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                    <div style={{ fontSize: '10px', textTransform: 'uppercase', marginBottom: '5px' }}>Anni di Gioco</div>
                    <input style={{ background: 'transparent', border: 'none', color: 'white', fontSize: '18px', fontWeight: 'bold', textAlign: 'center', width: '80%', outline: 'none' }} 
                           defaultValue={game.annoGiocato || ""} onBlur={(e) => modificaCampo(game.id, 'annoGiocato', e.target.value)} />
                    
                    <select style={{ marginTop: '15px', padding: '10px', borderRadius: '8px', border: 'none', fontWeight: 'bold', width: '80%' }} 
                            value={game.stato || 'Non Giocato'} onChange={(e) => cambiaStato(game.id, e.target.value)}>
                      {['Non Giocato', 'Completato', 'In corso', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>
                  </div>

                  <img src={game.copertina} style={{ position: 'relative', maxHeight: '100%', maxWidth: '100%', zIndex: 1 }} 
                       onError={(e) => e.target.src = 'https://via.placeholder.com/300x400?text=No+Cover'} alt={game.titolo} />
                </div>

                <div style={{ padding: '20px', flex: 1, display: 'flex', flexDirection: 'column' }}>
                  <h4 style={{ margin: '0 0 12px 0', fontSize: '17px', minHeight: '44px', lineHeight: '1.3' }}>{game.titolo}</h4>
                  
                  <div style={{ marginBottom: '12px' }}>
                    {piattaforme.map(p => <span key={p} className="platform-chip">{p}</span>)}
                  </div>

                  <div style={{ marginTop: 'auto', display: 'flex', justifyContent: 'space-between', fontSize: '11px', color: '#555', borderTop: '1px solid #2a2a2a', paddingTop: '10px' }}>
                    <span>Uscita: {game.annoUscita}</span>
                    <span style={{ fontStyle: 'italic', color: '#888' }}>{pulisciNomeSaga(game.saga)}</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default App;
