import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [loading, setLoading] = useState(true);

  // 1. Carica i dati dal backend
  const fetchGames = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  // 2. Upload Excel
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Lista aggiornata correttamente!");
    } catch (err) {
      alert("Errore nell'upload");
    } finally {
      setLoading(false);
    }
  };

  // 3. FUNZIONE SALVA MODIFICA (Corretta)
  const salvaModificaSaga = async (id, nuovaSaga) => {
    // Aggiorniamo localmente lo stato per velocità
    const listaAggiornata = games.map(g => g.id === id ? { ...g, saga: nuovaSaga } : g);
    setGames(listaAggiornata);
    
    try {
      // Inviamo la lista completa al server
      await axios.post('http://localhost:5000/api/games/update', listaAggiornata);
      console.log("Database sincronizzato");
    } catch (err) {
      alert("Errore durante il salvataggio sul server");
    }
  };

  // 4. Logica Filtri
  const sagheUniche = ['Tutte', ...new Set(games.map(g => g.saga))].filter(Boolean);
  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesSaga = selectedSaga === 'Tutte' || game.saga === selectedSaga;
    return matchesSearch && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#121212', color: 'white', fontFamily: 'Segoe UI' }}>
      
      {/* Sidebar */}
      <div style={{ width: '250px', padding: '20px', borderRight: '1px solid #333', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto', backgroundColor: '#181818' }}>
        <h3 style={{ borderBottom: '1px solid #333', paddingBottom: '10px', color: '#00d1b2' }}>Saghe</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {sagheUniche.map(s => (
            <li key={s} onClick={() => setSelectedSaga(s)} style={{ cursor: 'pointer', padding: '10px 8px', borderRadius: '4px', marginBottom: '4px', backgroundColor: selectedSaga === s ? '#00d1b222' : 'transparent', color: selectedSaga === s ? '#00d1b2' : '#bbb' }}>
              {s || "Senza Saga"}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', gap: '20px' }}>
          <input type="text" placeholder="Cerca titolo..." onChange={(e) => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '15px', background: '#222', color: 'white', border: '1px solid #444', borderRadius: '8px' }} />
          <input type="file" accept=".xlsx" onChange={handleFileUpload} />
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', color: '#00d1b2' }}>Caricamento...</div>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))', gap: '25px' }}>
            {filteredGames.map((game) => (
              <div key={game.id} style={{ background: '#1e1e1e', borderRadius: '12px', overflow: 'hidden', border: '1px solid #333' }}>
                <img src={game.copertina} alt={game.titolo} style={{ width: '100%', height: '280px', objectFit: 'cover' }} onError={(e) => { e.target.src = 'https://via.placeholder.com/200x280?text=No+Cover'; }} />
                <div style={{ padding: '15px' }}>
                  <h4 style={{ margin: '0 0 8px 0', fontSize: '15px' }}>{game.titolo}</h4>
                  
                  {/* INPUT PER MODIFICA SAGA */}
                  <div style={{ marginTop: '10px', borderTop: '1px solid #2a2a2a', paddingTop: '10px' }}>
                    <label style={{ fontSize: '10px', color: '#666', display: 'block' }}>SAGA (Clicca per modificare):</label>
                    <input 
                      type="text"
                      defaultValue={game.saga}
                      onBlur={(e) => salvaModificaSaga(game.id, e.target.value)}
                      style={{ 
                        background: 'transparent', 
                        border: 'none', 
                        color: '#00d1b2', 
                        fontSize: '12px', 
                        width: '100%', 
                        outline: 'none',
                        fontStyle: 'italic'
                      }}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
