import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [sortType, setSortType] = useState('titolo');
  const [loading, setLoading] = useState(true);

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    const sagaStr = String(saga).trim();
    const eccezioni = ["Remedy Connected Universe"];
    if (eccezioni.includes(sagaStr)) return sagaStr;
    return sagaStr.replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const fetchGames = async () => {
    try {
      setLoading(false); // Iniziamo il fetch
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) {
      console.error("Errore nel caricamento", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchGames(); }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      await axios.post('http://localhost:5000/api/upload', formData);
      await fetchGames();
      alert("Database sincronizzato!");
    } catch (err) { alert("Errore nell'upload"); }
  };

  const updateGameData = async (updatedList) => {
    try { await axios.post('http://localhost:5000/api/games/update', updatedList); } 
    catch (err) { console.error("Errore salvataggio"); }
  };

  const modificaCampo = (id, campo, valore) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const cambiaStato = (id, nuovoStato) => {
    const listaAggiornata = games.map(g => g.id === id ? { ...g, stato: nuovoStato } : g);
    setGames(listaAggiornata);
    updateGameData(listaAggiornata);
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return 'rgba(39, 174, 96, 0.85)';
      case 'In corso': return 'rgba(241, 196, 15, 0.85)';
      case 'Droppato': return 'rgba(231, 76, 60, 0.85)';
      case 'Sospeso': return 'rgba(230, 126, 34, 0.85)';
      default: return 'rgba(40, 40, 40, 0.85)';
    }
  };

  const resetFiltri = () => {
    setSelectedSaga('Tutte');
    setFilterYear('Tutti');
    setSearchTerm('');
  };

  const anniDisponibili = [...new Set(games.map(g => String(g.annoGiocato || "")))]
    .filter(a => a && a !== "-" && a.trim() !== "")
    .sort((a, b) => b - a);

  const saghePopolate = [...new Set(games.map(g => pulisciNomeSaga(g.saga)))]
    .filter(s => s && s !== "").sort();
  const saghePerSidebar = ['Tutte', 'Senza Saga', ...saghePopolate];

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const sagaVisualizzata = pulisciNomeSaga(game.saga);
      let matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !sagaVisualizzata : sagaVisualizzata === selectedSaga);
      let matchesYear = filterYear === 'Tutti' || String(game.annoGiocato) === filterYear;
      return matchesSearch && matchesSaga && matchesYear;
    })
    .sort((a, b) => {
      const convertiAnno = (val) => parseInt(val, 10) || 0;
      switch (sortType) {
        case 'titolo': return a.titolo.localeCompare(b.titolo);
        case 'uscita-desc': return convertiAnno(b.annoUscita) - convertiAnno(a.annoUscita);
        case 'uscita-asc': return convertiAnno(a.annoUscita) - convertiAnno(b.annoUscita);
        case 'giocato-desc': return convertiAnno(b.annoGiocato) - convertiAnno(a.annoGiocato);
        case 'giocato-asc': return convertiAnno(a.annoGiocato) - convertiAnno(b.annoGiocato);
        default: return 0;
      }
    });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI' }}>
      
      <style>{`
        .game-card { position: relative; overflow: hidden; border-radius: 12px; background: #181818; border: 1px solid #333; display: flex; flex-direction: column; transition: transform 0.2s; }
        .game-card:hover { transform: translateY(-3px); }
        .sidebar-item { cursor: pointer; padding: 10px 15px; border-radius: 8px; transition: 0.2s; margin-bottom: 2px; color: #888; font-size: 14px; }
        .sidebar-item:hover { color: #00d1b2; background: rgba(0, 209, 178, 0.05); }
        .sidebar-item.active { background: rgba(0, 209, 178, 0.15); color: #00d1b2; font-weight: bold; }
        .reset-btn { width: 100%; padding: 12px; background: #00d1b2; color: #000; border: none; border-radius: 8px; font-weight: bold; cursor: pointer; margin-bottom: 10px; transition: 0.3s; }
        .reset-btn:hover { background: #00b398; }
        .info-mask { position: absolute; top: 0; left: 0; right: 0; bottom: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; text-align: center; padding: 20px; backdrop-filter: blur(6px); }
        .game-card:hover .info-mask { opacity: 1; }
      `}</style>

      {/* Sidebar */}
      <div style={{ width: '280px', padding: '25px', borderRight: '1px solid #333', backgroundColor: '#111', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto' }}>
        <h2 style={{ color: '#00d1b2', fontSize: '1.4rem', marginBottom: '25px' }}>ðŸŽ® GameLib</h2>
        
        <button className="reset-btn" onClick={resetFiltri}>MOSTRA TUTTO</button>
        <div style={{ fontSize: '12px', color: '#555', textAlign: 'center', marginBottom: '25px' }}>
          Visualizzati: <b>{filteredGames.length}</b> / {games.length}
        </div>

        <p style={{ fontSize: '11px', color: '#555', textTransform: 'uppercase', letterSpacing: '1px', marginBottom: '10px' }}>Saghe</p>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {saghePerSidebar.map(s => (
            <li key={s} onClick={() => setSelectedSaga(s)} className={`sidebar-item ${selectedSaga === s ? 'active' : ''}`}>
              {s}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '40px', backgroundColor: '#0f0f0f' }}>
        
        {/* Toolbar */}
        <div style={{ marginBottom: '40px', display: 'flex', flexWrap: 'wrap', gap: '20px', alignItems: 'center' }}>
          <input type="text" value={searchTerm} placeholder="Cerca un titolo..." onChange={(e) => setSearchTerm(e.target.value)} 
                 style={{ flex: 1, minWidth: '200px', padding: '15px', background: '#1a1a1a', color: 'white', border: '1px solid #333', borderRadius: '12px', outline: 'none' }} />
          
          <select value={filterYear} onChange={(e) => setFilterYear(e.target.value)} 
                  style={{ padding: '15px', background: '#1a1a1a', color: '#00d1b2', border: '1px solid #333', borderRadius: '12px', cursor: 'pointer', fontWeight: 'bold' }}>
            <option value="Tutti">ðŸ“… Giocato: Sempre</option>
            {anniDisponibili.map(anno => <option key={anno} value={anno}>Giocato nel {anno}</option>)}
          </select>

          <select value={sortType} onChange={(e) => setSortType(e.target.value)} 
                  style={{ padding: '15px', background: '#1a1a1a', color: '#00d1b2', border: '1px solid #333', borderRadius: '12px', cursor: 'pointer', fontWeight: 'bold' }}>
            <option value="titolo">Ordina: A-Z</option>
            <option value="uscita-desc">Uscita: Recenti â†“</option>
            <option value="uscita-asc">Uscita: Storici â†‘</option>
            <option value="giocato-desc">Giocato: Ultimi â†“</option>
            <option value="giocato-asc">Giocato: Primi â†‘</option>
          </select>

          <div style={{ background: '#1a1a1a', padding: '8px 15px', borderRadius: '12px', border: '1px solid #333' }}>
            <input type="file" accept=".xlsx" onChange={handleFileUpload} style={{ fontSize: '11px', color: '#888' }} />
          </div>
        </div>

        {/* Grid */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '30px' }}>
          {filteredGames.map((game) => (
            <div key={game.id} className="game-card">
              {/* Media Section */}
              <div style={{ position: 'relative', height: '320px', background: '#000', display: 'flex', alignItems: 'center', justifyContent: 'center', overflow: 'hidden' }}>
                <img src={game.copertina} alt="" style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover', filter: 'blur(12px) brightness(0.4)' }} />
                <div className="info-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                  <span style={{ fontSize: '10px', textTransform: 'uppercase', marginBottom: '5px' }}>Giocato nel</span>
                  <input style={{ background: 'transparent', border: 'none', color: 'white', fontSize: '24px', fontWeight: 'bold', textAlign: 'center', width: '80%', outline: 'none' }} 
                         defaultValue={game.annoGiocato || ""} onBlur={(e) => modificaCampo(game.id, 'annoGiocato', e.target.value)} />
                  <select style={{ marginTop: '15px', padding: '8px', borderRadius: '6px', border: 'none', fontWeight: 'bold', width: '80%' }} 
                          value={game.stato || 'Non Giocato'} onChange={(e) => cambiaStato(game.id, e.target.value)}>
                    <option value="Non Giocato">Non Giocato</option>
                    <option value="Completato">Completato</option>
                    <option value="In corso">In corso</option>
                    <option value="Sospeso">Sospeso</option>
                    <option value="Droppato">Droppato</option>
                  </select>
                </div>
                <img src={game.copertina} alt={game.titolo} style={{ position: 'relative', maxHeight: '100%', maxWidth: '100%', zIndex: 1 }} 
                     onError={(e) => e.target.src = 'https://via.placeholder.com/260x320?text=No+Cover'} />
              </div>

              {/* Info Section */}
              <div style={{ padding: '20px' }}>
                <h4 style={{ margin: '0 0 10px 0', fontSize: '16px' }}>{game.titolo}</h4>
                <div style={{ fontSize: '12px', color: '#666' }}>
                  Uscita: <input style={{ background: 'transparent', border: 'none', color: '#888', width: '40px', outline: 'none' }} 
                                 defaultValue={game.annoUscita || ""} onBlur={(e) => modificaCampo(game.id, 'annoUscita', e.target.value)} />
                </div>
                <div style={{ fontSize: '11px', color: '#00d1b2', fontWeight: 'bold', marginTop: '5px' }}>
                  <input style={{ background: 'transparent', border: 'none', color: 'inherit', width: '100%', outline: 'none', fontWeight: 'bold' }} 
                         defaultValue={game.piattaforma || "Piattaforma"} onBlur={(e) => modificaCampo(game.id, 'piattaforma', e.target.value)} />
                </div>
                <div style={{ marginTop: '15px', paddingTop: '10px', borderTop: '1px solid #333' }}>
                  <input style={{ background: 'transparent', border: 'none', color: '#555', fontSize: '12px', width: '100%', outline: 'none', fontStyle: 'italic' }} 
                         defaultValue={pulisciNomeSaga(game.saga)} placeholder="Saga..." onBlur={(e) => modificaCampo(game.id, 'saga', e.target.value)} />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
