import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [sortType, setSortType] = useState('titolo');

  // --- LOGICA DI SEPARAZIONE (PIATTAFORME E ANNI) ---
  const dividiStringa = (valore) => {
    if (!valore) return [];
    const s = String(valore).trim();
    // Separa per virgola, spazio, slash o cambio Numero/Lettera
    return s.split(/[,/ \-]|(?<=[0-9])(?=[A-Z])|(?<=[a-zA-Z])(?=[0-9])/).filter(item => item.trim() !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return 'rgba(39, 174, 96, 0.9)';
      case 'In corso': return 'rgba(241, 196, 15, 0.9)';
      case 'Droppato': return 'rgba(231, 76, 60, 0.9)';
      case 'Sospeso': return 'rgba(230, 126, 34, 0.9)';
      default: return 'rgba(60, 60, 60, 0.9)';
    }
  };

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  // --- API ---
  const fetchGames = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const modificaCampo = async (id, campo, valore) => {
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    await axios.post('http://localhost:5000/api/games/update', nuovaLista);
  };

  // --- FILTRI ---
  const anniPerFiltro = [...new Set(games.flatMap(g => dividiStringa(g.annoGiocato)))]
    .filter(a => a.length === 4).sort((a, b) => b - a);

  const filteredGames = games.filter(game => {
    const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
    const anniGiocato = dividiStringa(game.annoGiocato);
    const matchesYear = filterYear === 'Tutti' || anniGiocato.includes(filterYear);
    const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
    return matchesSearch && matchesYear && matchesSaga;
  });

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI' }}>
      
      <style>{`
        .game-card { position: relative; border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; transition: 0.3s; }
        .card-rigiocato { border: 1px solid #f1c40f !important; }
        
        /* Chip Piattaforme (Verde Acqua) */
        .platform-chip { background: rgba(0, 209, 178, 0.15); color: #00d1b2; padding: 3px 8px; border-radius: 4px; font-size: 10px; font-weight: 800; margin-right: 5px; margin-bottom: 5px; display: inline-block; }
        
        /* Chip Anni (Grigio/Bianco) */
        .year-chip { background: #333; color: #eee; padding: 3px 8px; border-radius: 4px; font-size: 10px; font-weight: bold; margin-right: 5px; margin-bottom: 5px; display: inline-block; }
        
        .replay-badge { position: absolute; top: 10px; right: 10px; background: #f1c40f; color: #000; width: 26px; height: 26px; border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 14px; z-index: 10; }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(10px); }
        .game-card:hover .info-mask { opacity: 1; }
      `}</style>

      {/* Sidebar (Semplificata) */}
      <div style={{ width: '250px', padding: '20px', borderRight: '1px solid #333', background: '#111' }}>
        <h2 style={{ color: '#00d1b2', fontSize: '1.2rem' }}>ðŸŽ® MyLibrary</h2>
        <div style={{ marginTop: '20px' }}>
          <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#666' }} onClick={() => setSelectedSaga('Tutte')}>Tutte le Saghe</div>
          {[...new Set(games.map(g => pulisciNomeSaga(g.saga)))].filter(s => s).sort().map(s => (
            <div key={s} style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === s ? '#00d1b2' : '#666', fontSize: '14px' }} onClick={() => setSelectedSaga(s)}>{s}</div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
          <input type="text" placeholder="Cerca..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '12px', background: '#1a1a1a', border: '1px solid #333', borderRadius: '8px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ padding: '12px', background: '#1a1a1a', color: '#00d1b2', borderRadius: '8px', border: '1px solid #333' }}>
            <option value="Tutti">Anno: Tutti</option>
            {anniPerFiltro.map(a => <option key={a} value={a}>{a}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '25px' }}>
          {filteredGames.map(game => {
            const piattaforme = dividiStringa(game.piattaforma);
            const anniGiocati = dividiStringa(game.annoGiocato);
            const isRigiocato = anniGiocati.length > 1;

            return (
              <div key={game.id} className={`game-card ${isRigiocato ? 'card-rigiocato' : ''}`}>
                {isRigiocato && <div className="replay-badge">ðŸ”„</div>}
                
                <div style={{ position: 'relative', height: '320px', background: '#000' }}>
                  <img src={game.copertina} style={{ width: '100%', height: '100%', objectFit: 'cover' }} alt="" />
                  <div className="info-mask" style={{ backgroundColor: getColorStato(game.stato) }}>
                    <span style={{ fontSize: '10px', marginBottom: '5px' }}>MODIFICA ANNI</span>
                    <input 
                      style={{ background: 'transparent', border: 'none', color: 'white', fontSize: '16px', fontWeight: 'bold', textAlign: 'center', outline: 'none' }} 
                      defaultValue={game.annoGiocato} 
                      onBlur={e => modificaCampo(game.id, 'annoGiocato', e.target.value)} 
                    />
                    <select 
                      style={{ marginTop: '15px', padding: '5px', borderRadius: '5px' }}
                      value={game.stato} 
                      onChange={e => {
                        const nuovaLista = games.map(g => g.id === game.id ? { ...g, stato: e.target.value } : g);
                        setGames(nuovaLista);
                        axios.post('http://localhost:5000/api/games/update', nuovaLista);
                      }}
                    >
                      {['Non Giocato', 'Completato', 'In corso', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>
                  </div>
                </div>

                <div style={{ padding: '15px' }}>
                  <h4 style={{ margin: '0 0 10px 0', fontSize: '16px' }}>{game.titolo}</h4>
                  
                  {/* Sezione Chip Piattaforme */}
                  <div style={{ marginBottom: '5px' }}>
                    {piattaforme.map(p => <span key={p} className="platform-chip">{p}</span>)}
                  </div>

                  {/* Sezione Chip Anni di Gioco */}
                  <div style={{ marginBottom: '10px' }}>
                    {anniGiocati.map(a => <span key={a} className="year-chip">ðŸ“… {a}</span>)}
                  </div>

                  <div style={{ fontSize: '11px', color: '#444', borderTop: '1px solid #222', paddingTop: '10px', display: 'flex', justifyContent: 'space-between' }}>
                    <span>Uscita: {game.annoUscita}</span>
                    <span style={{ fontStyle: 'italic' }}>{pulisciNomeSaga(game.saga)}</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default App;
