import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [filterStatus, setFilterStatus] = useState('Tutti');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isMobileOpen, setIsMobileOpen] = useState(false);

  // Password e Accesso Admin
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey && (e.key === 'A' || e.key === 'a')) {
        const passwordSegreta = "listone";
        const inserimento = prompt("Accesso protetto. Inserisci codice:");
        if (inserimento === passwordSegreta) setIsAdmin(true);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('spinHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: ''
  });

  useEffect(() => { localStorage.setItem('spinHistory', JSON.stringify(history)); }, [history]);

  const fetchGames = async () => {
    try {
      const url = window.location.hostname === 'localhost' ? 'http://localhost:5000/api/games' : '/games.json';
      const res = await axios.get(url);
      setGames(res.data);
    } catch (err) { console.error("Errore caricamento dati:", err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const dividiStringa = (valore) => {
    if (!valore) return [];
    return String(valore).split(',').map(item => item.trim()).filter(item => item !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60';
      case 'In corso': return '#f1c40f';
      case 'Droppato': return '#e74c3c';
      case 'Sospeso': return '#e67e22';
      default: return '#7f8c8d';
    }
  };

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  const modificaCampo = async (id, campo, valore) => {
    if (!isAdmin) return;
    const nuovaLista = games.map(g => g.id === id ? { ...g, [campo]: String(valore).trim() } : g);
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
  };

  const aggiungiGioco = async (e) => {
    e.preventDefault();
    const nuovoGiocoConId = { ...newGame, id: Date.now() };
    const nuovaLista = [...games, nuovoGiocoConId];
    setGames(nuovaLista);
    if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
    }
    setNewGame({ titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato', note: '', categoria: '' });
    setShowAddForm(false);
  };

  const eliminaGioco = async (id, titolo) => {
    if (!isAdmin) return;
    if (window.confirm(`Eliminare "${titolo}"?`)) {
      const nuovaLista = games.filter(g => g.id !== id);
      setGames(nuovaLista);
      if (window.location.hostname === 'localhost') {
        await axios.post('http://localhost:5000/api/games/update', nuovaLista);
      }
    }
  };

  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    if (backlog.length === 0) return alert("Nessun gioco 'Non Giocato'!");
    setIsSpinning(true);
    setChosenGame(null);
    let durataTotale = 3000;
    let intervallo = 50;
    let tempoTrascorso = 0;
    const spin = () => {
      const randomTmp = backlog[Math.floor(Math.random() * backlog.length)];
      setSpinTitle(randomTmp.titolo);
      tempoTrascorso += intervallo;
      if (tempoTrascorso < durataTotale) setTimeout(spin, intervallo);
      else {
        const finale = backlog[Math.floor(Math.random() * backlog.length)];
        setChosenGame(finale);
        setHistory(prev => [finale.titolo, ...prev].slice(0, 5));
        setIsSpinning(false);
      }
    };
    spin();
  };

  const sagaStats = games.reduce((acc, g) => {
    const nome = pulisciNomeSaga(g.saga) || 'Senza Saga';
    acc[nome] = (acc[nome] || 0) + 1;
    return acc;
  }, { 'Senza Saga': 0 });

  const anniPerFiltro = [...new Set(games.flatMap(g => dividiStringa(g.annoGiocato)))].sort((a, b) => b - a);
  const suggerimentiSaghe = [...new Set(games.map(g => g.saga))].filter(s => s && s !== "-").sort();
  const suggerimentiPiattaforme = [...new Set(games.flatMap(g => dividiStringa(g.piattaforma)))].sort();

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesYear = filterYear === 'Tutti' || dividiStringa(game.annoGiocato).includes(filterYear);
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      const matchesStatus = filterStatus === 'Tutti' || game.stato === filterStatus;
      return matchesSearch && matchesYear && matchesSaga && matchesStatus;
    })
    .sort((a, b) => {
      if (selectedSaga !== 'Tutte' && selectedSaga !== 'Senza Saga') {
        return (parseInt(a.annoUscita) || 9999) - (parseInt(b.annoUscita) || 9999);
      }
      return a.titolo.localeCompare(b.titolo);
    });

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100%', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', overflow: 'hidden' }}>
      
      <datalist id="lista-saghe">{suggerimentiSaghe.map((s, i) => <option key={i} value={s} />)}</datalist>
      <datalist id="lista-piattaforme">{suggerimentiPiattaforme.map((p, i) => <option key={i} value={p} />)}</datalist>

      <style>{`
        * { box-sizing: border-box; }
        html, body { margin: 0; padding: 0; background-color: #0a0a0a; overflow: hidden; }
        ::-webkit-scrollbar { width: 8px; }
        ::-webkit-scrollbar-track { background: #0a0a0a; }
        ::-webkit-scrollbar-thumb { background: #222; border-radius: 10px; border: 2px solid #0a0a0a; }
        ::-webkit-scrollbar-thumb:hover { background: #00d1b2; }

        .game-card { position: relative; border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; display: flex; flex-direction: column; transition: transform 0.2s ease; }
        .game-card:hover { transform: translateY(-5px); border-color: #00d1b2; }
        .platform-chip { background: rgba(0, 209, 178, 0.15); color: #00d1b2; padding: 4px 10px; border-radius: 6px; font-size: 11px; font-weight: bold; margin-right: 6px; margin-bottom: 6px; display: inline-block; }
        .status-badge { font-size: 10px; text-transform: uppercase; font-weight: 900; padding: 2px 8px; border-radius: 4px; display: inline-block; margin-bottom: 8px; }
        .image-container { position: relative; height: 320px; background: #000; overflow: hidden; }
        .blur-bg { position: absolute; inset: -10px; background-size: cover; background-position: center; filter: blur(15px) brightness(0.3); z-index: 1; }
        .main-img { position: relative; width: 100%; height: 100%; object-fit: contain; z-index: 2; }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(8px); background: rgba(0,0,0,0.8); }
        .game-card:hover .info-mask { opacity: 1; }
        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.9); z-index: 1000; display: flex; align-items: center; justify-content: center; padding: 20px; }
        .modal-card { background: #1a1a1a; border-radius: 20px; border: 1px solid #333; width: 100%; max-width: 900px; display: flex; overflow: hidden; }
      `}</style>

      {/* Sidebar */}
      <div style={{ width: isCollapsed ? '70px' : '280px', padding: '20px', borderRight: '1px solid #333', background: '#111', flexShrink: 0, overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: isCollapsed ? 'center' : 'space-between', marginBottom: '25px' }}>
          {!isCollapsed && <h2 style={{ color: '#00d1b2', margin: 0, fontSize: '18px' }}>COLLEZIONE</h2>}
          <button onClick={() => setIsCollapsed(!isCollapsed)} style={{ background: '#222', border: 'none', color: '#00d1b2', cursor: 'pointer', borderRadius: '5px' }}>{isCollapsed ? 'âž¡' : 'â¬…'}</button>
        </div>
        {!isCollapsed && (
          <>
            <button className="btn-spin" onClick={avviaSpinner} style={{ background: 'linear-gradient(45deg, #f1c40f, #e67e22)', color: 'black', border: 'none', padding: '14px', borderRadius: '10px', fontWeight: '900', cursor: 'pointer', width: '100%', marginBottom: '15px' }}>ðŸŽ² COSA GIOCO?</button>
            {isAdmin && <button onClick={() => setShowAddForm(true)} style={{ width: '100%', padding: '12px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '20px' }}>+ AGGIUNGI GIOCO</button>}
            <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte le Saghe</div>
            {Object.keys(sagaStats).sort().map(s => (
              <div key={s} style={{ cursor: 'pointer', padding: '8px 10px', color: selectedSaga === s ? '#00d1b2' : '#888', fontSize: '14px' }} onClick={() => setSelectedSaga(s)}>
                {s} <span style={{ opacity: 0.5, fontSize: '11px' }}>({sagaStats[s]})</span>
              </div>
            ))}
          </>
        )}
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '40px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '40px' }}>
          <input type="text" placeholder="ðŸ” Cerca..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 2, padding: '15px', background: '#161616', border: '1px solid #333', borderRadius: '12px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333', fontWeight: 'bold' }}>
             <option value="Tutti">ðŸ“… Anno Giocato</option>
             {anniPerFiltro.map(y => <option key={y} value={y}>{y}</option>)}
          </select>
          <select value={filterStatus} onChange={e => setFilterStatus(e.target.value)} style={{ flex: 1, padding: '15px', background: '#161616', color: '#00d1b2', borderRadius: '12px', border: '1px solid #333', fontWeight: 'bold' }}>
            <option value="Tutti">ðŸ“Š Stato</option>
            {['Non Giocato', 'In corso', 'Completato', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div className="image-container">
                <div className="blur-bg" style={{ backgroundImage: `url(${game.copertina})` }}></div>
                <img src={game.copertina} className="main-img" alt="" />
                <div className="info-mask">
                  {isAdmin ? (
                    <div style={{ width: '90%', display: 'flex', flexDirection: 'column', gap: '8px' }}>
                      <button onClick={() => eliminaGioco(game.id, game.titolo)} style={{ background: '#e74c3c', color: 'white', border: 'none', padding: '8px', borderRadius: '5px', fontWeight: 'bold' }}>ELIMINA</button>
                      <select style={{ padding: '8px', borderRadius: '5px', background: getColorStato(game.stato), color: 'white', border: 'none' }} value={game.stato} onChange={e => modificaCampo(game.id, 'stato', e.target.value)}>
                        {['Non Giocato', 'Completato', 'In corso', 'Sospeso', 'Droppato'].map(s => <option key={s} value={s}>{s}</option>)}
                      </select>
                      <input list="lista-saghe" style={{ background: '#000', border: '1px solid #444', color: 'white', padding: '8px', borderRadius: '5px' }} defaultValue={game.saga} onBlur={e => modificaCampo(game.id, 'saga', e.target.value)} placeholder="Saga" />
                      <input style={{ background: '#000', border: '1px solid #444', color: 'white', padding: '8px', borderRadius: '5px' }} defaultValue={game.note} onBlur={e => modificaCampo(game.id, 'note', e.target.value)} placeholder="Note" />
                    </div>
                  ) : (
                    <div style={{ textAlign: 'center', padding: '20px' }}>
                      {game.saga && <h3 style={{ color: '#00d1b2', margin: '0' }}>{game.saga}</h3>}
                      <p>Giocato: {game.annoGiocato || '---'}</p>
                      <p style={{ fontSize: '12px', color: '#f1c40f' }}>{game.note}</p>
                    </div>
                  )}
                </div>
              </div>
              <div style={{ padding: '20px' }}>
                <div className="status-badge" style={{ backgroundColor: getColorStato(game.stato) }}>{game.stato}</div>
                <h4 style={{ margin: '0 0 10px 0', fontSize: '18px' }}>{game.titolo} {game.annoUscita && `(${game.annoUscita})`}</h4>
                {dividiStringa(game.piattaforma).map((p, i) => <span key={i} className="platform-chip">{p}</span>)}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal Aggiungi Gioco */}
      {showAddForm && (
        <div className="modal-overlay">
          <div className="modal-card">
            <div style={{ flex: 1, padding: '40px', borderRight: '1px solid #333' }}>
              <h2 style={{ color: '#00d1b2', marginTop: 0 }}>Nuovo Gioco</h2>
              <form onSubmit={aggiungiGioco}>
                <input placeholder="Titolo" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.titolo} onChange={e => setNewGame({...newGame, titolo: e.target.value})} />
                <input placeholder="Copertina URL" required style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.copertina} onChange={e => setNewGame({...newGame, copertina: e.target.value})} />
                <input placeholder="Saga" list="lista-saghe" style={{ width: '100%', padding: '12px', marginBottom: '10px', background: '#222', border: '1px solid #444', color: 'white', borderRadius: '8px' }} value={newGame.saga} onChange={e => setNewGame({...newGame, saga: e.target.value})} />
                <div style={{ display: 'flex', gap: '10px' }}>
                  <button type="button" onClick={() => setShowAddForm(false)} style={{ flex: 1, padding: '15px', background: '#333', color: 'white', border: 'none', borderRadius: '10px' }}>ANNULLA</button>
                  <button type="submit" style={{ flex: 1, padding: '15px', background: '#00d1b2', color: 'black', border: 'none', borderRadius: '10px', fontWeight: 'bold' }}>SALVA</button>
                </div>
              </form>
            </div>
            <div style={{ flex: 1, background: '#0d0d0d', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
               <p style={{ color: '#444', fontSize: '10px', textTransform: 'uppercase' }}>Preview</p>
               <div className="game-card" style={{ width: '250px' }}>
                  <div className="image-container"><img src={newGame.copertina} className="main-img" alt="" /></div>
                  <div style={{ padding: '15px' }}><h4>{newGame.titolo || 'Titolo...'}</h4></div>
               </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal Spinner */}
      {(isSpinning || chosenGame) && (
        <div className="modal-overlay">
          <div style={{ background: '#1a1a1a', padding: '40px', borderRadius: '20px', border: '2px solid #f1c40f', textAlign: 'center', maxWidth: '450px', width: '90%' }}>
            {isSpinning ? <h2>ESTRAZIONE: {spinTitle}</h2> : (
              <div>
                <h2 style={{ color: '#27ae60' }}>PROSSIMA AVVENTURA:</h2>
                <img src={chosenGame.copertina} style={{ width: '100%', borderRadius: '10px', margin: '20px 0' }} alt="" />
                <h1>{chosenGame.titolo}</h1>
                <button onClick={() => setChosenGame(null)} style={{ background: '#f1c40f', color: 'black', padding: '10px 30px', border: 'none', borderRadius: '8px', fontWeight: 'bold' }}>ANDIAMO!</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
