import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSaga, setSelectedSaga] = useState('Tutte');
  const [filterYear, setFilterYear] = useState('Tutti');
  const [showAddForm, setShowAddForm] = useState(false);
  
  // Stati Spinner
  const [isSpinning, setIsSpinning] = useState(false);
  const [chosenGame, setChosenGame] = useState(null);
  const [spinTitle, setSpinTitle] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('spinHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const [newGame, setNewGame] = useState({
    titolo: '', copertina: '', saga: '', annoUscita: '', annoGiocato: '', piattaforma: '', stato: 'Non Giocato'
  });

  useEffect(() => {
    localStorage.setItem('spinHistory', JSON.stringify(history));
  }, [history]);

  const fetchGames = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/games');
      setGames(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { fetchGames(); }, []);

  const dividiStringa = (valore) => {
    if (!valore) return [];
    return String(valore).split(',').map(item => item.trim()).filter(item => item !== "");
  };

  const getColorStato = (stato) => {
    switch (stato) {
      case 'Completato': return '#27ae60';
      case 'In corso': return '#f1c40f';
      case 'Droppato': return '#e74c3c';
      case 'Sospeso': return '#e67e22';
      default: return '#7f8c8d';
    }
  };

  const pulisciNomeSaga = (saga) => {
    if (!saga) return "";
    return String(saga).replace(/Series/gi, "").replace(/Saga/gi, "").trim();
  };

  // --- LOGICA SPINNER (5 SECONDI) ---
  const avviaSpinner = () => {
    const backlog = games.filter(g => g.stato === 'Non Giocato');
    if (backlog.length === 0) return alert("Nessun gioco 'Non Giocato'!");
    
    setIsSpinning(true);
    setChosenGame(null);
    
    let durataTotale = 5000; // 5 secondi
    let intervalloIniziale = 50;
    let tempoTrascorso = 0;

    const spin = () => {
      const randomTmp = backlog[Math.floor(Math.random() * backlog.length)];
      setSpinTitle(randomTmp.titolo);
      
      tempoTrascorso += intervalloIniziale;
      
      // Rallenta gradualmente verso la fine
      if (tempoTrascorso > 3500) intervalloIniziale += 15;
      if (tempoTrascorso > 4500) intervalloIniziale += 40;

      if (tempoTrascorso < durataTotale) {
        setTimeout(spin, intervalloIniziale);
      } else {
        const finale = backlog[Math.floor(Math.random() * backlog.length)];
        setChosenGame(finale);
        setHistory(prev => [finale.titolo, ...prev].slice(0, 5)); // Tiene gli ultimi 5
        setIsSpinning(false);
      }
    };

    spin();
  };

  const filteredGames = games
    .filter(game => {
      const matchesSearch = game.titolo?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesYear = filterYear === 'Tutti' || dividiStringa(game.annoGiocato).includes(filterYear);
      const matchesSaga = selectedSaga === 'Tutte' || (selectedSaga === 'Senza Saga' ? !pulisciNomeSaga(game.saga) : pulisciNomeSaga(game.saga) === selectedSaga);
      return matchesSearch && matchesYear && matchesSaga;
    })
    .sort((a, b) => a.titolo.localeCompare(b.titolo));

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100%', backgroundColor: '#0a0a0a', color: 'white', fontFamily: 'Segoe UI', overflow: 'hidden' }}>
      
      <style>{`
        .game-card { position: relative; border-radius: 12px; background: #161616; border: 1px solid #333; overflow: hidden; }
        .info-mask { position: absolute; inset: 0; display: flex; flex-direction: column; align-items: center; justify-content: center; opacity: 0; transition: 0.3s; z-index: 5; backdrop-filter: blur(10px); background: rgba(0,0,0,0.8); }
        .game-card:hover .info-mask { opacity: 1; }
        .modal-overlay { position: fixed; inset: 0; background: rgba(0,0,0,0.95); z-index: 999; display: flex; align-items: center; justify-content: center; }
        .modal-content { background: #1a1a1a; padding: 40px; border-radius: 20px; border: 2px solid #f1c40f; text-align: center; max-width: 450px; width: 90%; }
        .btn-spin { background: linear-gradient(45deg, #f1c40f, #e67e22); color: black; border: none; padding: 14px; border-radius: 10px; font-weight: 900; cursor: pointer; width: 100%; margin-bottom: 10px; text-transform: uppercase; }
        .history-item { font-size: 12px; color: #888; padding: 5px 0; border-bottom: 1px solid #222; }
      `}</style>

      {/* MODAL SPINNER */}
      {(isSpinning || chosenGame) && (
        <div className="modal-overlay">
          <div className="modal-content">
            {isSpinning ? (
              <div>
                <h2 style={{ color: '#f1c40f' }}>ANALISI BACKLOG...</h2>
                <div style={{ fontSize: '24px', fontWeight: 'bold', margin: '30px 0', color: '#fff' }}>{spinTitle}</div>
                <div className="loader" style={{ height: '4px', width: '100%', bg: '#333', position: 'relative' }}>
                    <div style={{ position: 'absolute', height: '100%', background: '#f1c40f', transition: 'width 5s linear', width: '100%' }}></div>
                </div>
              </div>
            ) : (
              <div>
                <h2 style={{ color: '#f1c40f' }}>DESTINO CONFERMATO:</h2>
                <img src={chosenGame.copertina} style={{ width: '100%', maxHeight: '300px', objectFit: 'contain', borderRadius: '10px' }} alt="" />
                <h1 style={{ fontSize: '28px', margin: '20px 0' }}>{chosenGame.titolo}</h1>
                <button onClick={() => setChosenGame(null)} style={{ background: '#f1c40f', color: 'black', border: 'none', padding: '12px 40px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' }}>RICEVUTO</button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Sidebar */}
      <div style={{ width: '280px', padding: '20px', borderRight: '1px solid #222', background: '#111', flexShrink: 0, overflowY: 'auto' }}>
        <h2 style={{ color: '#00d1b2' }}>ðŸŽ® My Collection</h2>
        
        <button className="btn-spin" onClick={avviaSpinner}>ðŸŽ² SPIN THE BACKLOG</button>

        {/* Cronologia degli Spin */}
        {history.length > 0 && (
          <div style={{ marginBottom: '20px', background: '#000', padding: '10px', borderRadius: '8px', border: '1px solid #222' }}>
            <div style={{ fontSize: '10px', color: '#f1c40f', fontWeight: 'bold', marginBottom: '8px', textTransform: 'uppercase' }}>Ultime Estrazioni</div>
            {history.map((h, i) => (
              <div key={i} className="history-item">ðŸŽ² {h}</div>
            ))}
            <button onClick={() => setHistory([])} style={{ background: 'none', border: 'none', color: '#555', fontSize: '10px', cursor: 'pointer', marginTop: '5px' }}>Svuota cronologia</button>
          </div>
        )}

        <button onClick={() => setShowAddForm(!showAddForm)} style={{ width: '100%', padding: '10px', background: 'transparent', color: '#00d1b2', border: '1px solid #00d1b2', borderRadius: '8px', cursor: 'pointer', marginBottom: '20px' }}>
          {showAddForm ? 'CHIUDI' : '+ AGGIUNGI'}
        </button>

        {/* ... (resto della sidebar con saghe e form rimasti invariati) */}
        <div style={{ marginTop: '20px' }}>
          <div style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === 'Tutte' ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga('Tutte')}>Tutte le Saghe</div>
          {[...new Set(games.map(g => pulisciNomeSaga(g.saga)))].filter(s => s).sort().map(s => (
            <div key={s} style={{ cursor: 'pointer', padding: '10px', color: selectedSaga === s ? '#00d1b2' : '#888' }} onClick={() => setSelectedSaga(s)}>{s}</div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px', overflowY: 'auto' }}>
        <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
          <input type="text" placeholder="Cerca titolo..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ flex: 1, padding: '12px', background: '#1a1a1a', border: '1px solid #333', borderRadius: '8px', color: 'white' }} />
          <select value={filterYear} onChange={e => setFilterYear(e.target.value)} style={{ padding: '12px', background: '#1a1a1a', color: '#00d1b2', borderRadius: '8px', border: '1px solid #333' }}>
            <option value="Tutti">Tutti gli Anni</option>
            {/* ... logic for years */}
          </select>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '25px' }}>
          {filteredGames.map(game => (
            <div key={game.id} className="game-card">
              <div style={{ position: 'relative', height: '340px', background: '#000' }}>
                <img src={game.copertina} style={{ width: '100%', height: '100%', objectFit: 'contain' }} alt={game.titolo} />
                <div className="info-mask">
                    {/* Logica di modifica rapida stato */}
                </div>
              </div>
              <div style={{ padding: '15px' }}>
                <div className="status-badge" style={{ backgroundColor: getColorStato(game.stato) }}>{game.stato}</div>
                <h4 style={{ margin: 0 }}>{game.titolo}</h4>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
